import React, { useState, useRef, useEffect } from 'react';
import * as XLSX from 'xlsx';
import { API_ENDPOINTS } from '../config';
import './CriarEtiqueta.css';

function CriarEtiqueta() {
  const [imei, setImei] = useState('');
  const [excelData, setExcelData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState({ total: 0, processed: 0, success: 0, errors: 0 });
  const [clusterId, setClusterId] = useState(null);
  const [isGeneratingQrCode, setIsGeneratingQrCode] = useState(false);
  const [qrCodeImage, setQrCodeImage] = useState(null);
  const fileInputRef = useRef(null);

  // Limpar o progresso quando os dados do Excel forem alterados
  useEffect(() => {
    if (excelData.length === 0) {
      setProgress({ total: 0, processed: 0, success: 0, errors: 0 });
    }
  }, [excelData]);

  const handleGenerateQrCode = async () => {
    if (!clusterId) {
      console.error('Nenhum clusterId disponível');
      alert('Erro: Nenhum clusterId disponível para gerar o QR code');
      return;
    }

    console.log('Iniciando geração do QR code para o cluster:', clusterId);
    setIsGeneratingQrCode(true);

    try {
      const url = API_ENDPOINTS.CLUSTER_QRCODE(clusterId);
      console.log('Fazendo requisição para:', url);

      // Adiciona um timestamp para evitar cache
      const timestamp = new Date().getTime();
      const urlWithCacheBust = `${url}${url.includes('?') ? '&' : '?'}_=${timestamp}`;

      console.log('URL com cache busting:', urlWithCacheBust);

      // Fazendo a requisição GET diretamente
      console.log('Fazendo requisição GET para o QR code...');
      const response = await fetch(urlWithCacheBust, {
        method: 'GET',
        headers: {
          'Accept': 'image/png,image/*;q=0.8',
          'Cache-Control': 'no-cache',
          'Pragma': 'no-cache'
        },
      });

      console.log('Resposta recebida. Status:', response.status);
      console.log('Cabeçalhos da resposta:', [...response.headers.entries()]);

      // Primeiro obtemos o texto da resposta para podermos ver o conteúdo
      const responseText = await response.text();

      // Se a resposta for HTML, provavelmente é uma página de erro
      if (responseText.trim().startsWith('<!DOCTYPE html>') || 
          responseText.includes('<html') || 
          responseText.includes('ngrok')) {
        console.error('Recebida resposta HTML em vez de imagem:', responseText);
        throw new Error('O servidor retornou uma página de erro HTML. Verifique se o endpoint está correto e acessível.');
      }

      // Se a resposta não for OK, lançamos um erro
      if (!response.ok) {
        console.error('Erro na resposta:', response.status, response.statusText, responseText);
        throw new Error(`Erro ao gerar QR code: ${response.status} - ${response.statusText || 'Erro desconhecido'}`);
      }

      // Verifica se o conteúdo é uma imagem
      const contentType = response.headers.get('content-type') || '';
      console.log('Tipo de conteúdo da resposta:', contentType);

      if (!contentType.includes('image/')) {
        console.error('Tipo de conteúdo inesperado:', contentType, 'Conteúdo:', responseText);
        throw new Error(`Tipo de conteúdo inesperado: ${contentType}. Esperado uma imagem.`);
      }

      // Se chegou até aqui, tenta criar o blob da imagem
      try {
        const blob = await response.blob();
        if (!blob || blob.size === 0) {
          throw new Error('A imagem recebida está vazia ou é inválida');
        }

        const blobUrl = URL.createObjectURL(blob);
        console.log('URL do blob criada com sucesso. Tamanho do blob:', blob.size, 'bytes');
        setQrCodeImage(blobUrl);
      } catch (blobError) {
        console.error('Erro ao processar a imagem:', blobError);
        throw new Error('Não foi possível processar a imagem do QR code: ' + blobError.message);
      }
    } catch (error) {
      console.error('Erro ao gerar QR code:', error);
      alert(`Erro ao gerar QR code: ${error.message}\n\nVerifique se o servidor está rodando e acessível.`);
    } finally {
      setIsGeneratingQrCode(false);
    }
  };

  const handleImeiChange = (e) => {
    setImei(e.target.value);
  };

  const enviarLoteParaAPI = async (lote, loteIndex, totalLotes) => {
    try {
      const response = await fetch(API_ENDPOINTS.CLUSTERS, {
        method: 'POST',
        headers: {
          'accept': 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          nome: `Lote ${loteIndex + 1} de ${totalLotes}`,
          imeis: lote,
          descricao: `Lote ${loteIndex + 1} de IMEIs processados`
        })
      });

      if (!response.ok) {
        throw new Error(`Erro no lote ${loteIndex + 1}: ${response.status}`);
      }

      const data = await response.json();
      if (data.cluster_id) {
        setClusterId(data.cluster_id);
      }
      return { success: true, data };
    } catch (error) {
      console.error(`Erro no lote ${loteIndex + 1}:`, error);
      return { success: false, error, imeis: lote };
    }
  };

  const processarIMEsEmLotes = async (imeis, tamanhoLote = 10) => {
    const lotes = [];
    for (let i = 0; i < imeis.length; i += tamanhoLote) {
      lotes.push(imeis.slice(i, i + tamanhoLote));
    }

    const resultados = [];
    const totalLotes = lotes.length;

    for (let i = 0; i < lotes.length; i++) {
      const lote = lotes[i];
      setProgress(prev => ({
        ...prev,
        loteAtual: i + 1,
        totalLotes
      }));

      const resultado = await enviarLoteParaAPI(lote, i, totalLotes);
      resultados.push(resultado);

      setProgress(prev => ({
        ...prev,
        processed: prev.processed + lote.length,
        success: resultado.success ? prev.success + lote.length : prev.success,
        errors: !resultado.success ? prev.errors + lote.length : prev.errors
      }));
    }

    return resultados;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isLoading) return;

    try {
      if (imei.trim()) {
        // Processar apenas o IMEI digitado
        const imeiUnico = imei.trim();
        console.log('Processando IMEI:', imeiUnico);

        setIsLoading(true);
        setProgress({
          total: 1,
          processed: 0,
          success: 0,
          errors: 0
        });

        // Criar um arquivo CSV temporário com o IMEI
        const csvContent = `IMEI\n${imeiUnico}`;
        const blob = new Blob([csvContent], { type: 'text/csv' });
        const file = new File([blob], 'imei.csv', { type: 'text/csv' });

        // Processar o arquivo CSV
        await processarArquivoCSV(file);
        setImei('');
      } else if (excelData.length > 0) {
        // Se houver dados do Excel, converter para CSV e enviar
        const csvContent = [
          'IMEI', // Cabeçalho
          ...excelData.map(item => (item.IMEI || item.imei || item.Imei || '').trim()).filter(Boolean)
        ].join('\n');

        const blob = new Blob([csvContent], { type: 'text/csv' });
        const file = new File([blob], 'imeis.csv', { type: 'text/csv' });

        await processarArquivoCSV(file);
        handleClearFile();
      } else {
        alert('Por favor, insira um IMEI, carregue um arquivo CSV ou Excel.');
      }
    } catch (error) {
      console.error('Erro ao processar:', error);
      alert(error.message || 'Ocorreu um erro ao processar. Verifique o console para mais detalhes.');
    } finally {
      setIsLoading(false);
    }
  };

  const processarArquivoCSV = async (file) => {
    setIsLoading(true);
    setProgress({
      total: 1,
      processed: 0,
      success: 0,
      errors: 0
    });

    const formData = new FormData();
    formData.append('file', file);

    try {
      const url = new URL(API_ENDPOINTS.PROCESSAR_CSV);
      url.searchParams.append('criar_cluster_automatico', 'true');
      url.searchParams.append('nome_cluster', 'Cluster gerado a partir de CSV');

      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'accept': 'application/json',
        },
        body: formData
      });

      if (!response.ok) {
        throw new Error(`Erro ao processar o arquivo: ${response.statusText}`);
      }

      const result = await response.json();
      console.log('Arquivo processado com sucesso:', result);

      // Atualiza o estado do clusterId se estiver na resposta
      if (result.cluster_id) {
        setClusterId(result.cluster_id);
      }

      setProgress(prev => ({
        ...prev,
        processed: 1,
        success: 1
      }));

      return result;
    } catch (error) {
      console.error('Erro ao processar o arquivo:', error);
      setProgress(prev => ({
        ...prev,
        errors: 1
      }));
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    try {
      // Verifica se é um arquivo CSV
      if (!file.name.endsWith('.csv') && !file.type.includes('csv')) {
        throw new Error('Por favor, selecione um arquivo CSV.');
      }

      // Processa o arquivo CSV diretamente
      await processarArquivoCSV(file);

      // Limpa o input de arquivo
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    } catch (error) {
      console.error('Erro:', error);
      alert(error.message || 'Erro ao processar o arquivo. Verifique o console para mais detalhes.');
    }
  };

  const handleClearFile = () => {
    setExcelData([]);
    setImei('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="criar-etiqueta">
      <h2>Gerar Etiqueta de IMEI</h2>

      <div className="importar-excel">
        <h3>Importar IMEIs do Excel/CSV</h3>
          <input
            type="file"
            accept=".csv, .xlsx, .xls"
            onChange={handleFileUpload}
            ref={fileInputRef}
            style={{ display: 'none' }}
            id="file-upload"
          />
        <div className="file-upload-container">
          <button 
            type="button" 
            className="file-upload-button"
            onClick={() => document.getElementById('file-upload').click()}
          >
            {isLoading ? 'Processando...' : 'Selecionar Arquivo (CSV/Excel)'}
          </button>
          {excelData.length > 0 && (
            <div className="file-info">
              <span>{excelData.length} IMEIs carregados</span>
              <button 
                type="button" 
                className="clear-button"
                onClick={handleClearFile}
              >
                Limpar
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="divider">ou</div>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="imei">IMEI:</label>
          <input
            type="text"
            id="imei"
            name="imei"
            value={imei}
            onChange={handleImeiChange}
            required={excelData.length === 0}
            disabled={excelData.length > 0}
            placeholder="Digite o número do IMEI"
          />
        </div>

        <div className="button-group">
          <button 
            type="submit" 
            className="submit-button"
            disabled={isLoading}
          >
            {isLoading ? (
              <span>Processando... {progress.processed}/{progress.total}</span>
            ) : excelData.length > 0 ? (
              `Gerar ${excelData.length} Etiquetas`
            ) : (
              'Processar IMEIs'
            )}
          </button>

          {/* Barra de progresso */}
          {isLoading && progress.total > 0 && (
            <div className="progress-container">
              <p>Processando: {progress.processed} de {progress.total} IMEIs</p>
              <div className="progress-bar">
                <div 
                  className="progress-bar-fill" 
                  style={{ width: `${(progress.processed / progress.total) * 100}%` }}
                ></div>
              </div>
              <p>Sucesso: {progress.success} | Erros: {progress.errors}</p>
              {progress.loteAtual && (
                <div className="progress-info">
                  Lote {progress.loteAtual} de {progress.totalLotes} • 
                  Sucessos: {progress.success} • 
                  Erros: {progress.errors}
                </div>
              )}
            </div>
          )}

          {/* Botão de Gerar QR Code - Sempre visível */}
          <div className="qr-code-button-container">
            <button 
              className={`criar-etiqueta-btn ${!clusterId || isGeneratingQrCode ? 'disabled' : ''}`}
              onClick={handleGenerateQrCode}
              disabled={!clusterId || isGeneratingQrCode}
              title={!clusterId ? 'Aguardando processamento...' : ''}
            >
              {isGeneratingQrCode ? 'Gerando QR Code...' : 'Gerar QR Code'}
            </button>

            {/* Exibição do QR Code */}
            {qrCodeImage && (
              <div className="qr-code-preview">
                <h4>QR Code Gerado:</h4>
                <img 
                  src={qrCodeImage} 
                  alt="QR Code do Cluster" 
                  className="qr-code-image"
                />
                <div className="qr-code-actions">
                  <a 
                    href={qrCodeImage} 
                    download={`qrcode-${clusterId}.png`}
                    className="download-qr-btn"
                  >
                    Baixar QR Code
                  </a>
                </div>
              </div>
            )}
          </div>
        </div>
      </form>
    </div>
  );
}

export default CriarEtiqueta;
