import React, { useState, useRef, useEffect, useCallback } from 'react';
import * as XLSX from 'xlsx';
import { API_ENDPOINTS } from '../config';
import './CriarEtiqueta.css';

function CriarEtiqueta() {
  const [imei, setImei] = useState('');
  const [excelData, setExcelData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState({ total: 0, processed: 0, success: 0, errors: 0 });
  const [clusterId, setClusterId] = useState(null);
  const [isGeneratingQrCode, setIsGeneratingQrCode] = useState(false);
  const [qrCodeImage, setQrCodeImage] = useState(null);

  // Função auxiliar para fazer o download da imagem
  const downloadImage = useCallback((url, filename) => {
    const link = document.createElement('a');
    link.href = url;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    // Libera a URL do objeto após o download
    if (url.startsWith('blob:')) {
      URL.revokeObjectURL(url);
    }
  }, []);
  const fileInputRef = useRef(null);

  // Limpar o progresso quando os dados do Excel forem alterados
  useEffect(() => {
    if (excelData.length === 0) {
      setProgress({ total: 0, processed: 0, success: 0, errors: 0 });
    }
  }, [excelData]);

  const handleGenerateQrCode = async () => {
    if (!clusterId) {
      console.error('Nenhum clusterId disponível');
      alert('Erro: Nenhum clusterId disponível para gerar o QR code');
      return;
    }

    console.log('Iniciando geração do QR code para o cluster:', clusterId);
    setIsGeneratingQrCode(true);

    try {
      // 1. Primeiro tenta a abordagem direta com CORS
      const url = API_ENDPOINTS.CLUSTER_QRCODE(clusterId);
      const timestamp = new Date().getTime();
      const urlWithCacheBust = `${url}${url.includes('?') ? '&' : '?'}_=${timestamp}`;

      console.log('Tentando carregar QR code de:', urlWithCacheBust);

      // Tenta primeiro com CORS
      try {
        // Primeiro faz uma requisição HEAD para verificar o tipo de conteúdo
        const headResponse = await fetch(urlWithCacheBust, {
          method: 'HEAD',
          headers: {
            'Accept': 'image/png,image/*;q=0.8',
            'Cache-Control': 'no-cache'
          },
          redirect: 'manual' // Não seguir redirecionamentos automaticamente
        });

        console.log('Resposta HEAD:', {
          status: headResponse.status,
          type: headResponse.type,
          redirected: headResponse.redirected,
          url: headResponse.url,
          headers: Object.fromEntries(headResponse.headers.entries())
        });

        // Se for um redirecionamento, pega a nova URL
        let finalUrl = urlWithCacheBust;
        if ([301, 302, 303, 307, 308].includes(headResponse.status)) {
          const location = headResponse.headers.get('location');
          if (location) {
            finalUrl = new URL(location, url).toString();
            console.log('Redirecionado para:', finalUrl);
          }
        }

        // Agora faz a requisição GET para a URL final
        const response = await fetch(finalUrl, {
          method: 'GET',
          headers: {
            'Accept': 'image/png,image/*;q=0.8',
            'Cache-Control': 'no-cache'
          }
        });

        const contentType = response.headers.get('content-type') || '';
        console.log('Content-Type da resposta:', contentType);

        if (response.ok) {
          // Se for uma imagem, processa normalmente
          if (contentType.startsWith('image/')) {
            const blob = await response.blob();
            if (blob && blob.size > 0) {
              const reader = new FileReader();
              return new Promise((resolve) => {
                reader.onloadend = () => {
                  setQrCodeImage(reader.result);
                  resolve();
                };
                reader.onerror = (error) => {
                  console.error('Erro ao ler o blob:', error);
                  const imageUrl = URL.createObjectURL(blob);
                  setQrCodeImage(imageUrl);
                  resolve();
                };
                reader.readAsDataURL(blob);
              });
            }
          } else {
            // Se não for uma imagem, tenta extrair a URL da imagem da resposta
            const text = await response.text();
            console.log('Resposta não é uma imagem:', text.substring(0, 200) + '...');

            // Tenta encontrar uma URL de imagem na resposta
            const imgMatch = text.match(/<img[^>]+src="([^">]+)"/i);
            if (imgMatch && imgMatch[1]) {
              const imgSrc = new URL(imgMatch[1], finalUrl).toString();
              console.log('Encontrada URL de imagem na resposta:', imgSrc);
              setQrCodeImage(imgSrc);
              return;
            }

            throw new Error('A resposta não contém uma imagem válida');
          }
        }
      } catch (corsError) {
        console.log('Erro na requisição CORS, tentando abordagem alternativa...', corsError);
      }

      // 2. Se a abordagem CORS falhar, tenta usar um iframe para exibir a URL
      console.log('Tentando carregar QR code em um iframe...');

      // Cria um iframe temporário para carregar a página
      const iframe = document.createElement('iframe');
      iframe.style.display = 'none';
      document.body.appendChild(iframe);

      return new Promise((resolve) => {
        iframe.onload = async () => {
          try {
            // Tenta capturar o conteúdo do iframe
            const iframeDoc = iframe.contentDocument || iframe.contentWindow.document;

            // Tenta encontrar uma imagem no iframe
            const img = iframeDoc.querySelector('img');
            if (img && img.src) {
              console.log('Imagem encontrada no iframe:', img.src);
              setQrCodeImage(img.src);
              document.body.removeChild(iframe);
              resolve();
              return;
            }

            // Se não encontrar, tiga o conteúdo do iframe
            console.log('Conteúdo do iframe:', iframeDoc.documentElement.outerHTML.substring(0, 500) + '...');

            // Tenta extrair a URL da imagem do HTML
            const html = iframeDoc.documentElement.outerHTML;
            const imgMatch = html.match(/<img[^>]+src=["']([^"']+)["']/i);
            if (imgMatch && imgMatch[1]) {
              const imgSrc = new URL(imgMatch[1], url).toString();
              console.log('URL da imagem extraída:', imgSrc);
              setQrCodeImage(imgSrc);
              document.body.removeChild(iframe);
              resolve();
              return;
            }

            throw new Error('Não foi possível encontrar uma imagem na resposta');
          } catch (e) {
            console.error('Erro ao processar iframe:', e);
            // Se tudo falhar, tenta usar a URL diretamente
            setQrCodeImage(urlWithCacheBust);
            document.body.removeChild(iframe);
            resolve();
          }
        };

        // Define um timeout para remover o iframe se algo der errado
        setTimeout(() => {
          if (document.body.contains(iframe)) {
            document.body.removeChild(iframe);
            setQrCodeImage(urlWithCacheBust);
            resolve();
          }
        }, 5000);

        // Carrega a URL no iframe
        iframe.src = urlWithCacheBust;
      });

    } catch (error) {
      console.error('Erro ao gerar QR code:', error);
      alert(`Erro ao gerar QR code: ${error.message}\n\nVerifique se o servidor está rodando e acessível.`);
    } finally {
      setIsGeneratingQrCode(false);
    }
  };

const handleImeiChange = (e) => {
    setImei(e.target.value);
  };

  const enviarLoteParaAPI = async (lote, loteIndex, totalLotes) => {
    try {
      const response = await fetch(API_ENDPOINTS.CLUSTERS, {
        method: 'POST',
        headers: {
          'accept': 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          nome: `Lote ${loteIndex + 1} de ${totalLotes}`,
          imeis: lote,
          descricao: `Lote ${loteIndex + 1} de IMEIs processados`
        })
      });

      if (!response.ok) {
        throw new Error(`Erro no lote ${loteIndex + 1}: ${response.status}`);
      }

      const data = await response.json();
      if (data.cluster_id) {
        setClusterId(data.cluster_id);
      }
      return { success: true, data };
    } catch (error) {
      console.error(`Erro no lote ${loteIndex + 1}:`, error);
      return { success: false, error, imeis: lote };
    }
  };

  const processarIMEsEmLotes = async (imeis, tamanhoLote = 10) => {
    const lotes = [];
    for (let i = 0; i < imeis.length; i += tamanhoLote) {
      lotes.push(imeis.slice(i, i + tamanhoLote));
    }

    const resultados = [];
    const totalLotes = lotes.length;

    for (let i = 0; i < lotes.length; i++) {
      const lote = lotes[i];
      setProgress(prev => ({
        ...prev,
        loteAtual: i + 1,
        totalLotes
      }));

      const resultado = await enviarLoteParaAPI(lote, i, totalLotes);
      resultados.push(resultado);

      setProgress(prev => ({
        ...prev,
        processed: prev.processed + lote.length,
        success: resultado.success ? prev.success + lote.length : prev.success,
        errors: !resultado.success ? prev.errors + lote.length : prev.errors
      }));
    }

    return resultados;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isLoading) return;

    try {
      if (imei.trim()) {
        // Processar apenas o IMEI digitado
        const imeiUnico = imei.trim();
        console.log('Processando IMEI:', imeiUnico);

        setIsLoading(true);
        setProgress({
          total: 1,
          processed: 0,
          success: 0,
          errors: 0
        });

        // Criar um arquivo CSV temporário com o IMEI
        const csvContent = `IMEI\n${imeiUnico}`;
        const blob = new Blob([csvContent], { type: 'text/csv' });
        const file = new File([blob], 'imei.csv', { type: 'text/csv' });

        // Processar o arquivo CSV
        await processarArquivoCSV(file);
        setImei('');
      } else if (excelData.length > 0) {
        // Se houver dados do Excel, converter para CSV e enviar
        const csvContent = [
          'IMEI', // Cabeçalho
          ...excelData.map(item => (item.IMEI || item.imei || item.Imei || '').trim()).filter(Boolean)
        ].join('\n');

        const blob = new Blob([csvContent], { type: 'text/csv' });
        const file = new File([blob], 'imeis.csv', { type: 'text/csv' });

        await processarArquivoCSV(file);
        handleClearFile();
      } else {
        alert('Por favor, insira um IMEI, carregue um arquivo CSV ou Excel.');
      }
    } catch (error) {
      console.error('Erro ao processar:', error);
      alert(error.message || 'Ocorreu um erro ao processar. Verifique o console para mais detalhes.');
    } finally {
      setIsLoading(false);
    }
  };

  const processarArquivoCSV = async (file) => {
    setIsLoading(true);
    setProgress({
      total: 1,
      processed: 0,
      success: 0,
      errors: 0
    });

    const formData = new FormData();
    formData.append('file', file);

    try {
      const url = new URL(API_ENDPOINTS.PROCESSAR_CSV);
      url.searchParams.append('criar_cluster_automatico', 'true');
      url.searchParams.append('nome_cluster', 'Cluster gerado a partir de CSV');

      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'accept': 'application/json',
        },
        body: formData
      });

      if (!response.ok) {
        throw new Error(`Erro ao processar o arquivo: ${response.statusText}`);
      }

      const result = await response.json();
      console.log('Arquivo processado com sucesso:', result);

      // Atualiza o estado do clusterId se estiver na resposta
      if (result.cluster_id) {
        setClusterId(result.cluster_id);
      }

      setProgress(prev => ({
        ...prev,
        processed: 1,
        success: 1
      }));

      return result;
    } catch (error) {
      console.error('Erro ao processar o arquivo:', error);
      setProgress(prev => ({
        ...prev,
        errors: 1
      }));
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    try {
      // Verifica se é um arquivo CSV
      if (!file.name.endsWith('.csv') && !file.type.includes('csv')) {
        throw new Error('Por favor, selecione um arquivo CSV.');
      }

      // Processa o arquivo CSV diretamente
      await processarArquivoCSV(file);

      // Limpa o input de arquivo
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    } catch (error) {
      console.error('Erro:', error);
      alert(error.message || 'Erro ao processar o arquivo. Verifique o console para mais detalhes.');
    }
  };

  const handleClearFile = () => {
    setExcelData([]);
    setImei('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="criar-etiqueta">
      <h2>Gerar Etiqueta de IMEI</h2>

      <div className="importar-excel">
        <h3>Importar IMEIs do Excel/CSV</h3>
          <input
            type="file"
            accept=".csv, .xlsx, .xls"
            onChange={handleFileUpload}
            ref={fileInputRef}
            style={{ display: 'none' }}
            id="file-upload"
          />
        <div className="file-upload-container">
          <button 
            type="button" 
            className="file-upload-button"
            onClick={() => document.getElementById('file-upload').click()}
          >
            {isLoading ? 'Processando...' : 'Selecionar Arquivo (CSV/Excel)'}
          </button>
          {excelData.length > 0 && (
            <div className="file-info">
              <span>{excelData.length} IMEIs carregados</span>
              <button 
                type="button" 
                className="clear-button"
                onClick={handleClearFile}
              >
                Limpar
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="divider">ou</div>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="imei">IMEI:</label>
          <input
            type="text"
            id="imei"
            name="imei"
            value={imei}
            onChange={handleImeiChange}
            required={excelData.length === 0}
            disabled={excelData.length > 0}
            placeholder="Digite o número do IMEI"
          />
        </div>

        <div className="button-group">
          <button 
            type="submit" 
            className="submit-button"
            disabled={isLoading}
          >
            {isLoading ? (
              <span>Processando... {progress.processed}/{progress.total}</span>
            ) : excelData.length > 0 ? (
              `Gerar ${excelData.length} Etiquetas`
            ) : (
              'Processar IMEIs'
            )}
          </button>

          {/* Barra de progresso */}
          {isLoading && progress.total > 0 && (
            <div className="progress-container">
              <p>Processando: {progress.processed} de {progress.total} IMEIs</p>
              <div className="progress-bar">
                <div 
                  className="progress-bar-fill" 
                  style={{ width: `${(progress.processed / progress.total) * 100}%` }}
                ></div>
              </div>
              <p>Sucesso: {progress.success} | Erros: {progress.errors}</p>
              {progress.loteAtual && (
                <div className="progress-info">
                  Lote {progress.loteAtual} de {progress.totalLotes} • 
                  Sucessos: {progress.success} • 
                  Erros: {progress.errors}
                </div>
              )}
            </div>
          )}

          {/* Botão de Gerar QR Code - Sempre visível */}
          <div className="qr-code-button-container">
            <button 
              className={`criar-etiqueta-btn ${!clusterId || isGeneratingQrCode ? 'disabled' : ''}`}
              onClick={handleGenerateQrCode}
              disabled={!clusterId || isGeneratingQrCode}
              title={!clusterId ? 'Aguardando processamento...' : ''}
            >
              {isGeneratingQrCode ? 'Gerando QR Code...' : 'Gerar QR Code'}
            </button>

            {/* Exibição do QR Code */}
            {qrCodeImage && (
              <div className="qr-code-preview">
                <h4>QR Code Gerado:</h4>
                <div style={{ border: '1px solid #ccc', padding: '10px', margin: '10px 0' }}>
                  <p>URL da imagem: {qrCodeImage.length > 50 ? qrCodeImage.substring(0, 50) + '...' : qrCodeImage}</p>
                  <p>Tipo: {qrCodeImage.startsWith('blob:') ? 'Blob URL' : 'URL direta'}</p>
                </div>
                <img 
                  src={qrCodeImage} 
                  alt="QR Code do Cluster" 
                  className="qr-code-image"
                  onLoad={(e) => console.log('Imagem carregada com sucesso!', e)}
                  onError={(e) => console.error('Erro ao carregar a imagem:', e)}
                  style={{ maxWidth: '100%', border: '1px solid red' }}
                />
                <div className="qr-code-actions">
                  <button 
                    className="download-qr-btn"
                    onClick={async (e) => {
                      e.preventDefault();
                      try {
                        console.log('Iniciando download do QR code:', qrCodeImage);

                        let imageUrl = qrCodeImage;

                        // Se for uma URL de dados (data:), já está pronta para download
                        if (qrCodeImage.startsWith('data:')) {
                          downloadImage(qrCodeImage, `qrcode-${clusterId}.png`);
                          return;
                        }

                        // Se for uma URL de blob, converte para blob e faz o download
                        if (qrCodeImage.startsWith('blob:')) {
                          const response = await fetch(qrCodeImage);
                          const blob = await response.blob();
                          const blobUrl = URL.createObjectURL(blob);
                          downloadImage(blobUrl, `qrcode-${clusterId}.png`);
                          return;
                        }

                        // Se for uma URL remota, baixa a imagem primeiro
                        const response = await fetch(qrCodeImage, {
                          mode: 'cors',
                          cache: 'no-cache'
                        });

                        if (!response.ok) {
                          throw new Error(`Erro ao baixar a imagem: ${response.status} ${response.statusText}`);
                        }

                        const blob = await response.blob();
                        const blobUrl = URL.createObjectURL(blob);
                        downloadImage(blobUrl, `qrcode-${clusterId}.png`);

                      } catch (error) {
                        console.error('Erro ao baixar o QR code:', error);
                        alert(`Erro ao baixar o QR code: ${error.message}`);
                      }
                    }}
                  >
                    Baixar QR Code
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </form>
    </div>
  );
}

export default CriarEtiqueta;
