import React, { useState, useRef, useEffect, useCallback } from 'react';
import './Consultar.css';
import { Html5Qrcode, Html5QrcodeSupportedFormats } from 'html5-qrcode';

// Configurações do scanner
const qrConfig = {
  fps: 10,
  qrbox: { width: 250, height: 250 },
  disableFlip: false,
  experimentalFeatures: { useBarCodeDetectorIfSupported: true },
  formatsToSupport: [
    Html5QrcodeSupportedFormats.QR_CODE,
    Html5QrcodeSupportedFormats.CODE_128,
    Html5QrcodeSupportedFormats.EAN_13,
  ],
};

function Consultar() {
  const [consulta, setConsulta] = useState('');
  const [resultado, setResultado] = useState(null);
  const [carregando, setCarregando] = useState(false);
  const [erroCamera, setErroCamera] = useState('');
  const [mensagemStatus, setMensagemStatus] = useState('');
  const [cameraAtiva, setCameraAtiva] = useState(false);
  const [iniciando, setIniciando] = useState(false);

  const [modeloSelecionado, setModeloSelecionado] = useState(null);
  const [imeisFiltrados, setImeisFiltrados] = useState([]);

  const html5QrCodeRef = useRef(null);
  const scannerRef = useRef(null);

  // Parar a câmera
  const pararCamera = useCallback(async () => {
    if (!html5QrCodeRef.current) return;

    try {
      console.log('Parando câmera...');
      await html5QrCodeRef.current.stop();
      console.log('Câmera parada com sucesso');
    } catch (err) {
      console.warn('Erro ao parar câmera:', err);
    } finally {
      html5QrCodeRef.current = null;
      setCameraAtiva(false);
    }
  }, []);

  // Iniciar a câmera
  const iniciarCamera = useCallback(async () => {
    if (iniciando || cameraAtiva) return;
    setIniciando(true);
    setErroCamera('');

    try {
      const scannerElement = scannerRef.current;
      if (!scannerElement) throw new Error('Elemento do scanner não encontrado');

      // Instancia o leitor — sem o segundo parâmetro!
      const qrCode = new Html5Qrcode(scannerElement.id);
      html5QrCodeRef.current = qrCode;

      // Inicia o scanner na câmera traseira
      await qrCode.start(
        { facingMode: 'environment' },
        qrConfig,
        (decodedText) => {
          console.log('Código detectado:', decodedText);
          setConsulta(decodedText);
          buscarProduto(decodedText);
          pararCamera();
        },
        (errorMessage) => {
          if (!errorMessage.includes('No MultiFormat')) {
            console.warn('Erro na leitura:', errorMessage);
          }
        }
      );

      setCameraAtiva(true);
      console.log('Scanner iniciado com sucesso!');
    } catch (error) {
      console.error('Erro ao iniciar câmera:', error);
      setErroCamera('Não foi possível acessar a câmera. Verifique as permissões.');
      await pararCamera();
    } finally {
      setIniciando(false);
    }
  }, [cameraAtiva, iniciando, pararCamera]);

  // Buscar informações do cluster
  const buscarProduto = async (clusterData) => {
    if (!clusterData?.trim()) return;

    try {
      setCarregando(true);
      setResultado(null);
      setErroCamera('');

      console.log('Dados recebidos do QR code:', clusterData);

      let clusterId;
      try {
        // Tenta extrair o ID da URL se for uma URL de QR code
        const urlMatch = clusterData.match(/clusters\/([^/]+)\/?/);
        if (urlMatch && urlMatch[1]) {
          clusterId = urlMatch[1];
        } else {
          // Se não for uma URL, tenta extrair o ID de um JSON ou objeto
          try {
            const parsed = typeof clusterData === 'string' ? JSON.parse(clusterData) : clusterData;
            // Verifica se é um objeto com id, cluster_id ou apenas o ID direto
            clusterId = parsed.id || parsed.cluster_id || parsed;
          } catch {
            clusterId = clusterData;
          }
        }
      } catch (e) {
        console.warn('Erro ao processar dados do QR code:', e);
        clusterId = clusterData;
      }

      const idLimpo = String(clusterId).trim();
      if (!idLimpo) throw new Error('ID do cluster inválido.');

      const baseUrl = process.env.REACT_APP_API_URL || 'http://127.0.0.1:8000';
      const apiUrl = `${baseUrl}/api/clusters/${idLimpo}`;
      console.log('Chamando API:', apiUrl);

      const response = await fetch(apiUrl, {
        method: 'GET',
        headers: {
          accept: 'application/json',
          'ngrok-skip-browser-warning': 'true',
        },
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || `Erro ${response.status}: ${response.statusText}`);
      }

      const dados = await response.json();
      console.log('Dados da API:', dados);

      // Garante que os dados estejam no formato esperado
      const resultadoFormatado = {
        ...dados,
        // Garante que os campos obrigatórios existam
        id: dados.id || idLimpo,
        nome: dados.nome || dados.cluster_nome || `Cluster ${idLimpo}`,
        descricao: dados.descricao || '',
        total_imeis: dados.total_imeis || (dados.detalhes_imeis ? Object.keys(dados.detalhes_imeis).length : 0),
        detalhes_imeis: dados.detalhes_imeis || {},
      };

      setResultado(resultadoFormatado);
      setModeloSelecionado(null);
      setImeisFiltrados([]);
    } catch (error) {
      console.error('Erro ao buscar informações:', error);
      setErroCamera(error.message);
    } finally {
      setCarregando(false);
    }
  };

  // Limpeza ao desmontar
  useEffect(() => {
    return () => {
      pararCamera();
    };
  }, [pararCamera]);

  return (
    <div className="consultar-container">
      <h1>Consultar Produto</h1>

      <div className="input-group">
        <input
          type="text"
          value={consulta}
          onChange={(e) => setConsulta(e.target.value)}
          placeholder="Digite o código ou escaneie o QR Code"
          className="form-control"
        />
        <button
          onClick={cameraAtiva ? pararCamera : iniciarCamera}
          className={`btn ${cameraAtiva ? 'btn-danger' : 'btn-primary'}`}
          disabled={iniciando}
        >
          {cameraAtiva ? 'Parar Câmera' : 'Escanear QR Code'}
        </button>
      </div>

      {erroCamera && <div className="alert alert-warning mt-3">{erroCamera}</div>}

      <div style={{ display: 'flex', justifyContent: 'center', margin: '20px 0' }}>
        <div
          id="scanner"
          ref={scannerRef}
          style={{
            width: 300,
            height: 300,
            display: cameraAtiva || iniciando ? 'block' : 'none',
            borderRadius: 12,
            overflow: 'hidden',
            margin: '0 auto',
          }}
        >
          {iniciando && (
            <div className="text-center">
              <div className="spinner-border text-primary" role="status" />
              <p>Preparando câmera...</p>
            </div>
          )}
        </div>
      </div>

      {carregando && (
        <div className="text-center">
          <div className="spinner-border text-primary" role="status" />
          <p>Buscando informações...</p>
        </div>
      )}

      {resultado && (
        <div className="card mt-4">
          <div className="card-body">
            <h3>{resultado.nome}</h3>
            <p className="text-muted">{resultado.descricao}</p>
            <p>Total de IMEIs: <strong>{resultado.total_imeis}</strong></p>

            <h4 className="mt-4">Modelos de Celulares</h4>
            <div className="table-responsive max-h-400">
              <table className="table table-striped">
                <thead className="table-dark">
                  <tr>
                    <th>Modelo</th>
                    <th>Quantidade</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(
                    Object.values(resultado.detalhes_imeis).reduce((acc, { modelo }) => {
                      const chave = `${modelo || 'Desconhecido'}`;
                      acc[chave] = (acc[chave] || 0) + 1;
                      return acc;
                    }, {})
                  )
                    .map(([modelo, quantidade]) => ({
                      modelo: modelo || 'Desconhecido',
                      quantidade,
                    }))
                    .sort((a, b) => b.quantidade - a.quantidade)
                    .map(({ modelo, quantidade }, index) => {
                      const ativo = modeloSelecionado === modelo;
                      return (
                        <tr
                          key={index}  
                          onClick={() => {
                            if (!resultado?.detalhes_imeis) return;

                            const entries = Array.isArray(resultado.detalhes_imeis)
                              ? resultado.detalhes_imeis
                              : Object.entries(resultado.detalhes_imeis).map(([_, v]) => v);

                            const lista = entries
                              .filter((item) => (item?.modelo || 'Desconhecido') === modelo);
                            if (modeloSelecionado === modelo) {
                              setModeloSelecionado(null);
                              setImeisFiltrados([]);
                            } else {
                              setModeloSelecionado(modelo);
                              setImeisFiltrados(lista);
                            }
                          }}
                          style={{ cursor: 'pointer', backgroundColor: ativo ? '#eef2ff' : undefined }}
                        >
                          <td>{modelo || 'Desconhecido'}</td>
                          <td style={{ textAlign: 'right' }}>{quantidade}</td>
                        </tr>
                      );
                    })}
                </tbody>
              </table>
            </div>

            {modeloSelecionado && (
              <div className="mt-3">
                <h5>
                  IMEIs do modelo {modeloSelecionado} ({imeisFiltrados.length})
                </h5>
                <div className="table-responsive max-h-400">
                  <table className="table">
                    <thead>
                      <tr>
                        <th>IMEI</th>
                        <th>IMEI2</th>
                        <th>Número de Série</th>
                      </tr>
                    </thead>
                    <tbody>
                      {imeisFiltrados.map((item, idx) => {
                        // Encontra os detalhes completos do item em resultado.detalhes_imeis
                        const detalhes = Array.isArray(resultado.detalhes_imeis) 
                          ? resultado.detalhes_imeis.find(d => d.imei === item)
                          : resultado.detalhes_imeis[item];

                        return (
                          <tr key={idx}>
                            <td>{detalhes?.imei || item}</td>
                            <td>{detalhes?.imei2 || 'N/A'}</td>
                            <td>{detalhes?.serial || 'N/A'}</td>
                          </tr>
                        );
                      })}
        </tbody>
      </table>
    </div>
  </div>
)}
          </div>
        </div>
      )}
    </div>
  );
}

export default Consultar;
