// Configuração da API
const isDevelopment = process.env.NODE_ENV === 'development';

// Get the current hostname to check if we're running through ngrok
const isNgrok = window.location.hostname.includes('ngrok-free.app') || 
                window.location.hostname.includes('ngrok.io');

// URL base da API
// 1. Se existir uma variável de ambiente REACT_APP_API_URL, ela tem prioridade
// 2. Se estivermos no ngrok, usamos o localhost (o backend precisa estar acessível no mesmo domínio para evitar CORS)
// 3. Se não, verifica se estamos em desenvolvimento e usa o localhost
// 4. Senão, usa a mesma origem do frontend (produção)
const API_BASE_URL = process.env.REACT_APP_API_URL 
  ? process.env.REACT_APP_API_URL
  : isNgrok
    ? 'http://localhost:8000'  // Usar localhost quando estiver no ngrok
    : isDevelopment 
      ? 'http://127.0.0.1:8000'  // Local
      : window.location.origin;   // Produção (mesma origem do frontend)

const API_ENDPOINTS = {
  CLUSTERS: `${API_BASE_URL}/clusters`,
  CLUSTER_QRCODE: (clusterId) => `${API_BASE_URL}/clusters/${clusterId}/qrcode`,
  PROCESSAR_CSV: `${API_BASE_URL}/processar-csv`
};

const config = {
  API_BASE_URL,
  ...API_ENDPOINTS
};

export { API_ENDPOINTS };
export default config;
