import React, { useState, useRef, useEffect, useCallback } from 'react';
import './Consultar.css';
import { Html5Qrcode, Html5QrcodeSupportedFormats } from 'html5-qrcode';

// Configurações do scanner
const qrConfig = {
  fps: 10,
  qrbox: { width: 250, height: 250 },
  disableFlip: false,
  experimentalFeatures: { useBarCodeDetectorIfSupported: true },
  formatsToSupport: [
    Html5QrcodeSupportedFormats.QR_CODE,
    Html5QrcodeSupportedFormats.CODE_128,
    Html5QrcodeSupportedFormats.EAN_13
  ]
};

function Consultar() {
  const [consulta, setConsulta] = useState('');
  const [resultado, setResultado] = useState(null);
  const [carregando, setCarregando] = useState(false);
  const [erroCamera, setErroCamera] = useState('');
  const [cameraAtiva, setCameraAtiva] = useState(false);
  const [iniciando, setIniciando] = useState(false);

  const html5QrCodeRef = useRef(null);
  const scannerRef = useRef(null);

  // --- Parar a câmera com limpeza segura ---
  const pararCamera = useCallback(async () => {
    if (!html5QrCodeRef.current) return;

    try {
      console.log('Parando câmera...');
      await html5QrCodeRef.current.stop();
      await html5QrCodeRef.current.clear(); // limpa os elementos internos
      console.log('Câmera parada e limpa com sucesso');
    } catch (err) {
      console.warn('Erro ao parar câmera:', err);
    } finally {
      html5QrCodeRef.current = null;
      setCameraAtiva(false);
    }
  }, []);

  // --- Iniciar a câmera ---
  const iniciarCamera = useCallback(async () => {
    if (iniciando || cameraAtiva) return;
    setIniciando(true);
    setErroCamera('');

    try {
      const scannerElement = scannerRef.current;
      if (!scannerElement) throw new Error('Elemento do scanner não encontrado');

      // Instancia o leitor
      const qrCode = new Html5Qrcode(scannerElement.id, true);
      html5QrCodeRef.current = qrCode;

      // Inicia o scanner na câmera traseira
      await qrCode.start(
        { facingMode: 'environment' },
        qrConfig,
        (decodedText) => {
          console.log('Código detectado:', decodedText);
          setConsulta(decodedText);
          buscarProduto(decodedText);
          pararCamera();
        },
        (errorMessage) => {
          if (!errorMessage.includes('No MultiFormat')) {
            console.warn('Erro na leitura:', errorMessage);
          }
        }
      );

      setCameraAtiva(true);
      console.log('Scanner iniciado com sucesso!');
    } catch (error) {
      console.error('Erro ao iniciar câmera:', error);
      setErroCamera('Não foi possível acessar a câmera. Verifique as permissões.');
      await pararCamera();
    } finally {
      setIniciando(false);
    }
  }, [cameraAtiva, iniciando, pararCamera]);

  // --- Buscar informações do cluster ---
  const buscarProduto = async (clusterData) => {
    if (!clusterData?.trim()) return;

    try {
      setCarregando(true);
      setResultado(null);
      setErroCamera('');

      console.log('Dados recebidos do QR code:', clusterData);

      // Tenta fazer parse do JSON se for uma string JSON
      let clusterId;
      try {
        const parsedData = JSON.parse(clusterData);
        clusterId = parsedData.id || clusterData; // Usa o ID do JSON ou o próprio dado se não for JSON
      } catch (e) {
        // Se não for JSON, usa o valor direto
        clusterId = clusterData;
      }

      // Remove espaços em branco e caracteres inválidos
      const idLimpo = clusterId.trim();

      if (!idLimpo) {
        throw new Error('ID do cluster não encontrado no QR code');
      }

      console.log('ID do cluster extraído:', idLimpo);

      // Monta a URL da API
      const baseUrl = process.env.REACT_APP_API_URL || 'http://127.0.0.1:8000';
      const apiUrl = `${baseUrl}/api/clusters/${idLimpo}`;

      console.log('URL da requisição:', apiUrl);

      const response = await fetch(apiUrl, {
        method: 'GET',
        headers: {
          'accept': 'application/json',
          'Content-Type': 'application/json',
          'ngrok-skip-browser-warning': 'true'
        },
        credentials: 'include' // Importante para enviar cookies se necessário
      });

      console.log('Status da resposta:', response.status);

      if (!response.ok) {
        const errorData = await response.text();
        console.error('Erro na resposta:', errorData);
        throw new Error(`Erro ${response.status}: ${response.statusText}`);
      }

      const dados = await response.json();
      console.log('Dados recebidos:', dados);

      // Salva todos os campos retornados pela API
      setResultado({
        ...dados, // Espalha todos os campos recebidos
        // Garante que campos específicos estejam no formato correto
        dataCriacao: dados.data_criacao ? new Date(dados.data_criacao).toLocaleString() : 'Não informado',
        totalImeis: dados.total_imeis || 0,
        imeis: dados.imeis || [],
        detalhesImeis: dados.detalhes_imeis || {}
      });

    } catch (error) {
      console.error('Erro ao buscar informações do cluster:', error);
      setErroCamera(`Erro ao buscar informações: ${error.message || 'Tente novamente mais tarde.'}`);
    } finally {
      setCarregando(false);
    }
  };

  // --- Limpeza ao desmontar ---
  useEffect(() => {
    return () => {
      console.log('Desmontando componente...');
      pararCamera();
    };
  }, [pararCamera]);

  return (
    <div className="consultar-container">
      <h1>Consultar Produto</h1>

      {/* Input manual */}
      <div className="input-group">
        <input
          type="text"
          value={consulta}
          onChange={(e) => setConsulta(e.target.value)}
          placeholder="Digite o código ou escaneie o QR Code"
          className="form-control"
        />
        <button
          onClick={cameraAtiva ? pararCamera : iniciarCamera}
          className={`btn ${cameraAtiva ? 'btn-danger' : 'btn-primary'}`}
          disabled={iniciando}
        >
          {cameraAtiva ? 'Parar Câmera' : 'Escanear QR Code'}
        </button>
      </div>

      {/* Mensagem de erro */}
      {erroCamera && (
        <div className="alert alert-warning mt-3">{erroCamera}</div>
      )}

      {/* Scanner */}
      <div style={{ display: 'flex', justifyContent: 'center', margin: '20px 0' }}>
        <div
          id="scanner"
          ref={scannerRef}
          className={`scanner-container ${cameraAtiva ? 'active' : ''}`}
          style={{
            width: '300px',
            height: '300px',
            display: cameraAtiva || iniciando ? 'block' : 'none',
            borderRadius: '12px',
            overflow: 'hidden',
            position: 'relative',
            margin: '0 auto'
          }}
        >
        {iniciando && (
          <div className="scanner-loading text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Carregando câmera...</span>
            </div>
            <p>Preparando a câmera...</p>
          </div>
        )}
        </div>
      </div>

      {/* Carregando produto */}
      {carregando && (
        <div className="text-center mt-3">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Carregando...</span>
          </div>
          <p>Buscando informações do produto...</p>
        </div>
      )}

      {/* Resultado */}
      {resultado && (
        <div className="resultado-consulta mt-4 p-3 border rounded">
          <h3 className="mb-3">Informações do Cluster</h3>

          {/* Seção de propriedades gerais */}
          <div className="mb-4">
            <h5>Detalhes Gerais</h5>
            <div className="table-responsive">
              <table className="table table-bordered">
                <tbody>
                  {Object.entries(resultado)
                    // Filtra campos que não são arrays ou objetos
                    .filter(([key]) => !['imeis', 'detalhesImeis', 'totalImeis'].includes(key))
                    .map(([key, value]) => (
                      <tr key={key}>
                        <th style={{ width: '200px' }}>
                          {key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}:
                        </th>
                        <td>
                          {typeof value === 'boolean' 
                            ? value ? 'Sim' : 'Não'
                            : value || 'N/A'}
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
          </div>

          {/* Seção de IMEIs */}
          {resultado.imeis && resultado.imeis.length > 0 && (
            <div className="mt-4">
              <h5>Dispositivos no Cluster ({resultado.imeis.length})</h5>
              <div className="table-responsive">
                <table className="table table-striped">
                  <thead>
                    <tr>
                      <th>IMEI</th>
                      {resultado.detalhesImeis && Object.keys(resultado.detalhesImeis[resultado.imeis[0]] || {}).map(key => (
                        <th key={key}>
                          {key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}
                        </th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    {resultado.imeis.map(imei => {
                      const detalhes = resultado.detalhesImeis?.[imei] || {};
                      return (
                        <tr key={imei}>
                          <td>{imei}</td>
                          {Object.values(detalhes).map((valor, idx) => (
                            <td key={idx}>
                              {typeof valor === 'boolean' 
                                ? valor ? 'Sim' : 'Não'
                                : valor || 'N/A'}
                            </td>
                          ))}
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Consultar;
