import React, { useState, useRef, useEffect, useCallback } from 'react';
import './Consultar.css';
import { Html5Qrcode, Html5QrcodeSupportedFormats } from 'html5-qrcode';

// Configurações do scanner
const qrConfig = {
  fps: 10,
  qrbox: { width: 250, height: 250 },
  disableFlip: false,
  experimentalFeatures: { useBarCodeDetectorIfSupported: true },
  formatsToSupport: [
    Html5QrcodeSupportedFormats.QR_CODE,
    Html5QrcodeSupportedFormats.CODE_128,
    Html5QrcodeSupportedFormats.EAN_13
  ]
};

function Consultar() {
  const [consulta, setConsulta] = useState('');
  const [resultado, setResultado] = useState(null);
  const [carregando, setCarregando] = useState(false);
  const [erroCamera, setErroCamera] = useState('');
  const [cameraAtiva, setCameraAtiva] = useState(false);
  const [iniciando, setIniciando] = useState(false);

  const html5QrCodeRef = useRef(null);
  const scannerRef = useRef(null);

  // --- Parar a câmera com limpeza segura ---
  const pararCamera = useCallback(async () => {
    if (!html5QrCodeRef.current) return;

    try {
      console.log('Parando câmera...');
      await html5QrCodeRef.current.stop();
      await html5QrCodeRef.current.clear(); // limpa os elementos internos
      console.log('Câmera parada e limpa com sucesso');
    } catch (err) {
      console.warn('Erro ao parar câmera:', err);
    } finally {
      html5QrCodeRef.current = null;
      setCameraAtiva(false);
    }
  }, []);

  // --- Iniciar a câmera ---
  const iniciarCamera = useCallback(async () => {
    if (iniciando || cameraAtiva) return;
    setIniciando(true);
    setErroCamera('');

    try {
      const scannerElement = scannerRef.current;
      if (!scannerElement) throw new Error('Elemento do scanner não encontrado');

      // Instancia o leitor
      const qrCode = new Html5Qrcode(scannerElement.id, true);
      html5QrCodeRef.current = qrCode;

      // Inicia o scanner na câmera traseira
      await qrCode.start(
        { facingMode: 'environment' },
        qrConfig,
        (decodedText) => {
          console.log('Código detectado:', decodedText);
          setConsulta(decodedText);
          buscarProduto(decodedText);
          pararCamera();
        },
        (errorMessage) => {
          if (!errorMessage.includes('No MultiFormat')) {
            console.warn('Erro na leitura:', errorMessage);
          }
        }
      );

      setCameraAtiva(true);
      console.log('Scanner iniciado com sucesso!');
    } catch (error) {
      console.error('Erro ao iniciar câmera:', error);
      setErroCamera('Não foi possível acessar a câmera. Verifique as permissões.');
      await pararCamera();
    } finally {
      setIniciando(false);
    }
  }, [cameraAtiva, iniciando, pararCamera]);

  // --- Buscar informações do cluster ---
  const buscarProduto = async (clusterData) => {
    if (!clusterData?.trim()) return;

    try {
      setCarregando(true);
      setResultado(null);
      setErroCamera('');

      console.log('Dados recebidos do QR code:', clusterData);

      // Tenta fazer parse do JSON se for uma string JSON
      let clusterId;
      try {
        const parsedData = JSON.parse(clusterData);
        clusterId = parsedData.id || clusterData; // Usa o ID do JSON ou o próprio dado se não for JSON
      } catch (e) {
        // Se não for JSON, usa o valor direto
        clusterId = clusterData;
      }

      // Remove espaços em branco e caracteres inválidos
      const idLimpo = clusterId.trim();

      if (!idLimpo) {
        throw new Error('ID do cluster não encontrado no QR code');
      }

      console.log('ID do cluster extraído:', idLimpo);

      // Monta a URL da API
      const baseUrl = process.env.REACT_APP_API_URL || 'http://127.0.0.1:8000';
      const apiUrl = `${baseUrl}/api/clusters/${idLimpo}`;

      console.log('URL da requisição:', apiUrl);

      const response = await fetch(apiUrl, {
        method: 'GET',
        headers: {
          'accept': 'application/json',
          'Content-Type': 'application/json',
          'ngrok-skip-browser-warning': 'true'
        },
        credentials: 'include' // Importante para enviar cookies se necessário
      });

      console.log('Status da resposta:', response.status);

      if (!response.ok) {
        const errorData = await response.text();
        console.error('Erro na resposta:', errorData);
        throw new Error(`Erro ${response.status}: ${response.statusText}`);
      }

      const dados = await response.json();
      console.log('Dados recebidos:', JSON.stringify(dados, null, 2));

      // Função para formatar valores para exibição
      const formatarValor = (valor) => {
        if (valor === null || valor === undefined) return 'N/A';
        if (typeof valor === 'boolean') return valor ? 'Sim' : 'Não';
        if (typeof valor === 'object') return JSON.stringify(valor);
        return valor;
      };

      // Cria um novo objeto com os dados formatados
      const dadosFormatados = {};

      // Copia todas as propriedades do objeto original
      Object.entries(dados).forEach(([chave, valor]) => {
        // Converte chaves com underscore para camelCase
        const chaveFormatada = chave.replace(/_([a-z])/g, (g) => g[1].toUpperCase());

        // Formata valores especiais
        if (chave === 'data_criacao') {
          dadosFormatados.dataCriacao = valor ? new Date(valor).toLocaleString() : 'Não informado';
        } else if (chave === 'detalhes_imeis') {
          // Formata os detalhes dos IMEIs
          const detalhesFormatados = {};
          Object.entries(valor || {}).forEach(([imei, detalhe]) => {
            detalhesFormatados[imei] = {};
            Object.entries(detalhe || {}).forEach(([detalheChave, detalheValor]) => {
              detalhesFormatados[imei][detalheChave] = formatarValor(detalheValor);
            });
          });
          dadosFormatados.detalhesImeis = detalhesFormatados;
        } else {
          dadosFormatados[chaveFormatada] = formatarValor(valor);
        }
      });

      console.log('Dados formatados:', dadosFormatados);
      setResultado(dadosFormatados);

    } catch (error) {
      console.error('Erro ao buscar informações do cluster:', error);
      setErroCamera(`Erro ao buscar informações: ${error.message || 'Tente novamente mais tarde.'}`);
    } finally {
      setCarregando(false);
    }
  };

  // --- Limpeza ao desmontar ---
  useEffect(() => {
    return () => {
      console.log('Desmontando componente...');
      pararCamera();
    };
  }, [pararCamera]);

  return (
    <div className="consultar-container">
      <h1>Consultar Produto</h1>

      {/* Input manual */}
      <div className="input-group">
        <input
          type="text"
          value={consulta}
          onChange={(e) => setConsulta(e.target.value)}
          placeholder="Digite o código ou escaneie o QR Code"
          className="form-control"
        />
        <button
          onClick={cameraAtiva ? pararCamera : iniciarCamera}
          className={`btn ${cameraAtiva ? 'btn-danger' : 'btn-primary'}`}
          disabled={iniciando}
        >
          {cameraAtiva ? 'Parar Câmera' : 'Escanear QR Code'}
        </button>
      </div>

      {/* Mensagem de erro */}
      {erroCamera && (
        <div className="alert alert-warning mt-3">{erroCamera}</div>
      )}

      {/* Scanner */}
      <div style={{ display: 'flex', justifyContent: 'center', margin: '20px 0' }}>
        <div
          id="scanner"
          ref={scannerRef}
          className={`scanner-container ${cameraAtiva ? 'active' : ''}`}
          style={{
            width: '300px',
            height: '300px',
            display: cameraAtiva || iniciando ? 'block' : 'none',
            borderRadius: '12px',
            overflow: 'hidden',
            position: 'relative',
            margin: '0 auto'
          }}
        >
        {iniciando && (
          <div className="scanner-loading text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Carregando câmera...</span>
            </div>
            <p>Preparando a câmera...</p>
          </div>
        )}
        </div>
      </div>

      {/* Carregando produto */}
      {carregando && (
        <div className="text-center mt-3">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Carregando...</span>
          </div>
          <p>Buscando informações do produto...</p>
        </div>
      )}

      {/* Resultado */}
      {resultado && (
        <div className="resultado-consulta mt-4">
          {/* Cabeçalho com informações do cluster */}
          <div className="card mb-4">
            <div className="card-header bg-primary text-white">
              <h4 className="mb-0">
                <i className="bi bi-box-seam me-2"></i>
                {resultado.nome || 'Detalhes da Caixa'}
              </h4>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-md-6">
                  <p><strong>ID:</strong> <span className="text-muted">{resultado.id || 'N/A'}</span></p>
                  <p><strong>Descrição:</strong> {resultado.descricao || 'Nenhuma descrição fornecida'}</p>
                </div>
                <div className="col-md-6">
                  <p><strong>Data de Criação:</strong> {resultado.dataCriacao || 'N/A'}</p>
                  <p><strong>Total de Itens:</strong> 
                    <span className="badge bg-primary">
                      {resultado.totalImeis || 0} {resultado.totalImeis === 1 ? 'item' : 'itens'}
                    </span>
                  </p>
                </div>
              </div>
            </div>
          </div>

          {/* Seção de Itens na Caixa */}
          <div className="mb-4">
            <div className="d-flex justify-content-between align-items-center mb-3">
              <h4 className="mb-0">
                <i className="bi bi-phone me-2"></i>
                Itens na Caixa
              </h4>
              <span className="badge bg-primary">
                {resultado.imeis?.length || 0} itens
              </span>
            </div>

            {/* Cards dos dispositivos */}
            <div className="row g-3">
              {Object.entries(resultado.detalhesImeis || {}).map(([imei, detalhes]) => {
                // Conta quantos itens iguais existem
                const quantidade = resultado.imeis?.filter(i => i === imei).length || 1;

                return (
                  <div key={imei} className="col-12 col-sm-6 col-lg-4">
                    <div className="card h-100 border-primary">
                      <div className="card-header bg-light">
                        <div className="d-flex justify-content-between align-items-center">
                          <h5 className="mb-0">
                            <i className="bi bi-phone me-2"></i>
                            {detalhes.modelo || 'Dispositivo'}
                          </h5>
                          {quantidade > 1 && (
                            <span className="badge bg-primary rounded-pill">
                              {quantidade}x
                            </span>
                          )}
                        </div>
                      </div>
                      <div className="card-body">
                        <ul className="list-unstyled mb-0">
                          <li className="mb-2">
                            <small className="text-muted">IMEI:</small>
                            <div className="text-truncate" style={{maxWidth: '100%'}}>
                              <code>{imei}</code>
                            </div>
                          </li>
                          {Object.entries(detalhes || {}).map(([chave, valor]) => {
                            if (chave === 'modelo') return null; // Já mostramos o modelo no título

                            let valorFormatado = valor;
                            if (valor === null || valor === undefined) {
                              valorFormatado = 'N/A';
                            } else if (typeof valor === 'object') {
                              valorFormatado = JSON.stringify(valor);
                            }

                            return (
                              <li key={chave} className="mb-1">
                                <small className="text-muted">
                                  {chave.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}:
                                </small>
                                <div className="text-truncate" style={{maxWidth: '100%'}}>
                                  {valorFormatado.toString()}
                                </div>
                              </li>
                            );
                          })}
                        </ul>
                      </div>
                    </div>
                  </div>
                );
              })}

              {/* Mensagem quando não há itens */}
              {(!resultado.detalhesImeis || Object.keys(resultado.detalhesImeis).length === 0) && (
                <div className="col-12">
                  <div className="alert alert-info mb-0">
                    <i className="bi bi-info-circle me-2"></i>
                    Nenhum item encontrado nesta caixa.
                  </div>
                </div>
              )}
            </div>

            {/* Resumo por modelo */}
            {resultado.detalhesImeis && Object.keys(resultado.detalhesImeis).length > 0 && (
              <div className="mt-4">
                <h5 className="mb-3">
                  <i className="bi bi-list-ul me-2"></i>
                  Resumo por Modelo
                </h5>
                <div className="table-responsive">
                  <table className="table table-sm">
                    <thead>
                      <tr>
                        <th>Modelo</th>
                        <th className="text-end">Quantidade</th>
                        <th>Detalhes</th>
                      </tr>
                    </thead>
                    <tbody>
                      {Object.entries(
                        // Agrupa por modelo
                        Object.entries(resultado.detalhesImeis || {}).reduce((acc, [imei, detalhes]) => {
                          const modelo = detalhes.modelo || 'Sem modelo';
                          if (!acc[modelo]) {
                            acc[modelo] = { quantidade: 0, imeis: [] };
                          }
                          acc[modelo].quantidade += resultado.imeis?.filter(i => i === imei).length || 1;
                          acc[modelo].imeis.push(imei);
                          return acc;
                        }, {})
                      ).map(([modelo, dados]) => (
                        <tr key={modelo}>
                          <td>{modelo}</td>
                          <td className="text-end">
                            <span className="badge bg-primary">
                              {dados.quantidade} {dados.quantidade === 1 ? 'unidade' : 'unidades'}
                            </span>
                          </td>
                          <td>
                            <small className="text-muted">
                              {dados.imeis.length} {dados.imeis.length === 1 ? 'IMEI' : 'IMEIs diferentes'}
                            </small>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Consultar;
