import React, { useMemo, useState } from 'react';
import config from '../config';

function CriarEtiqueta() {
  const [imei, setImei] = useState('');
  const [nome, setNome] = useState('');
  const [descricao, setDescricao] = useState('');
  const [tocado, setTocado] = useState(false);
  const [criando, setCriando] = useState(false);
  const [erro, setErro] = useState('');
  const [cluster, setCluster] = useState(null);
  const [showQr, setShowQr] = useState(false);
  const [qrLoading, setQrLoading] = useState(false);
  const [qrError, setQrError] = useState('');
  const [qrStamp, setQrStamp] = useState('');
  const [qrSrc, setQrSrc] = useState('');

  const valido = useMemo(() => {
    if (!/^[0-9]{15}$/.test(imei)) return false;
    let sum = 0;
    for (let i = 0; i < 14; i++) {
      let digit = parseInt(imei[i], 10);
      if (i % 2 === 1) {
        digit *= 2;
        if (digit > 9) digit -= 9;
      }
      sum += digit;
    }
    const check = (10 - (sum % 10)) % 10;
    return check === parseInt(imei[14], 10);
  }, [imei]);

  const podeCriar = valido && nome.trim().length > 0 && !criando;

  const handleCriar = async () => {
    if (!podeCriar) return;
    setErro('');
    setCriando(true);
    setCluster(null);
    try {
      const baseUrl = config.API_BASE_URL;
      const url = `${baseUrl}/api/clusters/?nome=${encodeURIComponent(nome)}&descricao=${encodeURIComponent(descricao || '')}`;
      const resp = await fetch(url, {
        method: 'POST',
        headers: {
          accept: 'application/json',
          'Content-Type': 'application/json',
          'ngrok-skip-browser-warning': 'true',
        },
        body: JSON.stringify([imei])
      });
      if (!resp.ok) {
        const text = await resp.text();
        throw new Error(`Erro ${resp.status}: ${text || resp.statusText}`);
      }
      const data = await resp.json();
      setCluster(data);
      setShowQr(false);
      setQrError('');
      if (qrSrc) { URL.revokeObjectURL(qrSrc); setQrSrc(''); }

    } catch (e) {
      setErro(e.message || 'Erro ao criar cluster');
    } finally {
      setCriando(false);
    }
  };

  const handleGerarQr = async () => {
    if (!cluster?.id) return;
    try {
      setQrLoading(true);
      setQrError('');
      // Chamada explícita ao endpoint para gerar/obter o QR (com cache-busting)
      const stamp = String(Date.now());
      const url = `${config.CLUSTER_QRCODE(String(cluster.id))}?ngrok-skip-browser-warning=true&t=${stamp}`;
      // Log para depuração
      // eslint-disable-next-line no-console
      console.log('Gerar QR GET:', url);
      const res = await fetch(url, {
        method: 'GET',
        headers: { accept: '*/*' },
        cache: 'no-store'
      });
      if (!res.ok) {
        const text = await res.text();
        throw new Error(text || `Erro ao gerar QR (${res.status})`);
      }
      if (qrSrc) { URL.revokeObjectURL(qrSrc); }
      const blob = await res.blob();
      const objUrl = URL.createObjectURL(blob);
      setQrSrc(objUrl);
      setQrStamp(stamp);
      setShowQr(true);
    } catch (e) {
      setQrError(e.message || 'Falha ao gerar QR Code');
      setShowQr(false);
    } finally {
      setQrLoading(false);
    }
  };

  const handlePrint = async () => {
    const idParaQr = cluster?.id ? String(cluster.id) : null;
    if (!idParaQr) return;
    const w = window.open('', '_blank', 'width=600,height=400');
    if (!w) return;
    const qrcodeUrl = qrSrc || `${config.CLUSTER_QRCODE(idParaQr)}?ngrok-skip-browser-warning=true&t=${qrStamp || Date.now()}`;
    const html = `<!doctype html>
<html>
<head>
<meta charset="utf-8" />
<title>Etiqueta Cluster</title>
<style>
  body { font-family: Arial, sans-serif; margin: 0; padding: 16px; }
  .label { width: 340px; border: 1px solid #111; padding: 16px; border-radius: 8px; }
  .title { font-size: 14px; color: #333; margin: 0 0 8px; }
  .main { display: flex; align-items: center; gap: 12px; }
  .big { font-size: 18px; font-weight: 700; letter-spacing: 0.5px; margin: 6px 0 6px; }
  .small { font-size: 12px; color: #555; }
  .qr { width: 120px; height: 120px; object-fit: contain; }
  @media print { body { padding: 0; } .label { border: none; } }
  .divider { height: 1px; background: #ddd; margin: 8px 0; }
</style>
</head>
<body onload="window.print()">
  <div class="label">
    <h2 class="title">Etiqueta de Cluster</h2>
    <div class="main">
      <img class="qr" src="${qrcodeUrl}" alt="QR" />
      <div>
        <div class="big">Cluster: ${cluster?.id ?? '-'}</div>
        <div class="small">Nome: ${nome ? nome.replace(/</g,'&lt;') : '-'}</div>
        <div class="small">IMEI: ${imei}</div>
        <div class="small">Gerado: ${new Date().toLocaleString()}</div>
      </div>
    </div>
    ${descricao ? `<div class="divider"></div><div class="small">${descricao.replace(/</g,'&lt;')}</div>` : ''}
  </div>
</body>
</html>`;
    w.document.open();
    w.document.write(html);
    w.document.close();
  };

  const handleBaixarQr = async () => {
    try {
      if (!cluster?.id) return;
      const url = `${config.CLUSTER_QRCODE(String(cluster.id))}?ngrok-skip-browser-warning=true&t=${qrStamp || Date.now()}`;
      const fileName = `qr-cluster-${cluster.id}.png`;
      try {
        const a = document.createElement('a');
        a.href = url;
        a.download = fileName;
        a.rel = 'noopener';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        return;
      } catch {}
      try {
        const r = await fetch(qrSrc || url, { headers: { accept: '*/*' }, cache: 'no-store' });
        if (!r.ok) throw new Error(String(r.status));
        const blob = await r.blob();
        const a = document.createElement('a');
        a.href = URL.createObjectURL(blob);
        a.download = fileName;
        a.rel = 'noopener';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(a.href);
        return;
      } catch {}
      window.open(url, '_blank');
    } catch (e) {
      setQrError(e.message || 'Falha ao baixar QR Code');
    }
  };

  // Removido pré-carregamento do QR: usamos <img src> direto para garantir que o endpoint seja chamado

  return (
    <div className="criar-etiqueta" style={{ maxWidth: 520, margin: '0 auto' }}>
      <h1>Criar Etiqueta por IMEI</h1>
      <div className="form" style={{ display: 'grid', gap: 12 }}>
        <input
          type="text"
          placeholder="Nome do cluster"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          className="form-control"
          style={{ padding: '10px 12px', fontSize: 16 }}
        />
        <input
          type="text"
          placeholder="Descrição (opcional)"
          value={descricao}
          onChange={(e) => setDescricao(e.target.value)}
          className="form-control"
          style={{ padding: '10px 12px', fontSize: 16 }}
        />
        <input
          type="text"
          inputMode="numeric"
          placeholder="Digite o IMEI (15 dígitos)"
          value={imei}
          onChange={(e) => setImei(e.target.value.replace(/\D/g, ''))}
          onBlur={() => setTocado(true)}
          className="form-control"
          style={{ padding: '10px 12px', fontSize: 16 }}
        />
        {tocado && imei && !valido && (
          <div className="alert alert-warning">IMEI inválido. Verifique os 15 dígitos.</div>
        )}
        {erro && (
          <div className="alert alert-warning">{erro}</div>
        )}
        <div style={{ display: 'flex', gap: 8 }}>
          <button
            disabled={!podeCriar}
            onClick={handleCriar}
            className={`btn ${podeCriar ? 'btn-primary' : 'btn-secondary'}`}
            style={{ padding: '10px 14px', fontWeight: 600 }}
          >
            {criando ? 'Criando...' : 'Criar Cluster'}
          </button>
          {cluster && (
            <button
              onClick={handleGerarQr}
              className="btn btn-outline-primary"
              style={{ padding: '10px 14px', fontWeight: 600 }}
              disabled={qrLoading}
            >
              {qrLoading ? 'Gerando QR...' : 'Gerar QR Code'}
            </button>
          )}
          {cluster && showQr && (
            <button
              onClick={handleBaixarQr}
              className="btn btn-outline-secondary"
              style={{ padding: '10px 14px', fontWeight: 600 }}
            >
              Baixar QR Code
            </button>
          )}
          <button
            disabled={!cluster}
            onClick={handlePrint}
            className={`btn ${cluster ? 'btn-outline-primary' : 'btn-secondary'}`}
            style={{ padding: '10px 14px', fontWeight: 600 }}
          >
            Imprimir Etiqueta
          </button>
        </div>
      </div>
      {qrError && (
        <div className="alert alert-warning" style={{ marginTop: 12 }}>{qrError}</div>
      )}
      {cluster && showQr && (
        <div className="preview" style={{ marginTop: 20 }}>
          <div style={{ display: 'inline-block', border: '1px solid #ccc', borderRadius: 8, padding: 12 }}>
            <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
              <img
                className="qr"
                width={100}
                height={100}
                alt="QR Cluster"
                src={qrSrc || `${config.CLUSTER_QRCODE(String(cluster.id))}?ngrok-skip-browser-warning=true&t=${qrStamp || Date.now()}`}
                key={cluster.id}
              />
              <div>
                <div style={{ fontSize: 14, color: '#666' }}>Cluster: {cluster?.id ?? '-'}</div>
                <div style={{ fontSize: 18, fontWeight: 700 }}>IMEI: {imei}</div>
                {nome && <div style={{ fontSize: 12, color: '#666' }}>Nome: {nome}</div>}
                {descricao && <div style={{ fontSize: 12, color: '#666' }}>Desc: {descricao}</div>}
                <div style={{ fontSize: 12, color: '#666', marginTop: 6 }}>
                  URL do QR: <a href={config.CLUSTER_QRCODE(String(cluster.id))} target="_blank" rel="noreferrer">{config.CLUSTER_QRCODE(String(cluster.id))}</a>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default CriarEtiqueta;
