import React, { useState, useRef, useEffect } from 'react';
import './Consultar.css';
import { Html5Qrcode, Html5QrcodeSupportedFormats } from 'html5-qrcode';
import { API_ENDPOINTS } from '../config';

function Consultar() {
  const [consulta, setConsulta] = useState('');
  const [resultado, setResultado] = useState(null);
  const [carregando, setCarregando] = useState(false);
  const [mostrarCamera, setMostrarCamera] = useState(false);
  const [erroCamera, setErroCamera] = useState('');
  const html5QrCodeRef = useRef(null);

  const handleChange = (e) => {
    setConsulta(e.target.value);
  };

  useEffect(() => {
    // Limpar o scanner quando o componente for desmontado
    return () => {
      if (html5QrCodeRef.current && html5QrCodeRef.current.isScanning) {
        html5QrCodeRef.current.stop().catch(error => {
          console.error('Erro ao parar o scanner:', error);
        });
      }
    };
  }, []);

  const iniciarCamera = async () => {
    try {
      setMostrarCamera(true);
      setErroCamera('Iniciando...');

      // Configuração ultra-rápida
      const config = { 
        fps: 60,
        qrbox: 250,
        videoConstraints: {
          facingMode: 'environment',
          width: { ideal: 1920 },
          height: { ideal: 1080 },
          frameRate: { ideal: 60, max: 60 }
        }
      };

      // Limpa instância anterior se existir
      if (html5QrCodeRef.current) {
        try {
          await html5QrCodeRef.current.stop();
        } catch (e) {
          console.warn('Erro ao parar scanner anterior:', e);
        }
      }

      // Cria nova instância
      html5QrCodeRef.current = new Html5Qrcode('scanner');

      // Função para processar o código escaneado
      const onScanSuccess = (decodedText) => {
        setConsulta(decodedText);
        pararCamera();
        buscarProduto(decodedText);
      };

      // Tenta câmera traseira primeiro
      try {
        await html5QrCodeRef.current.start(
          { facingMode: 'environment' },
          config,
          onScanSuccess,
          undefined
        );
      } catch (err) {
        console.warn('Tentando câmera frontal...');
        try {
          await html5QrCodeRef.current.start(
            { facingMode: 'user' },
            config,
            onScanSuccess,
            undefined
          );
        } catch (frontError) {
          setErroCamera('Não foi possível acessar a câmera');
        }
      }
    } catch (err) {
      setErroCamera('Erro ao acessar a câmera');
    }
  };

  const pararCamera = async () => {
    if (html5QrCodeRef.current) {
      try {
        await html5QrCodeRef.current.stop();
        await html5QrCodeRef.current.clear();
        html5QrCodeRef.current = null;
      } catch (err) {
        console.error('Erro ao parar a câmera:', err);
      } finally {
        setMostrarCamera(false);
      }
    } else {
      setMostrarCamera(false);
    }
  };

  const contarModelosUnicos = (imeis) => {
    if (!imeis || !Array.isArray(imeis)) return 0;
    const modelos = new Set(imeis.map(imei => imei.modelo || '').filter(Boolean));
    return modelos.size;
  };

  const processarDadosDoCluster = (dados) => {
    if (!dados || typeof dados !== 'object') {
      return {
        erro: 'Dados inválidos',
        detalhes: 'Os dados recebidos não estão no formato esperado'
      };
    }

    // Garante que imeis seja um array
    let imeis = [];
    if (Array.isArray(dados.imeis)) {
      imeis = dados.imeis;
    } else if (dados.imei) {
      // Se for um único IMEI, converte para array
      imeis = [dados.imei];
    }

    const quantidadeImeis = imeis.length;
    const quantidadeModelos = contarModelosUnicos(imeis);

    // Cria o objeto de retorno com valores padrão
    const resultado = {
      id: dados.id || 'N/A',
      nome: dados.nome || 'Sem nome',
      descricao: dados.descricao || 'Sem descrição',
      quantidadeImeis,
      quantidadeModelos,
      dataCriacao: dados.data_criacao || new Date().toISOString().split('T')[0],
      imeis: []
    };

    // Adiciona os IMEIs formatados apenas se houver itens
    if (imeis.length > 0) {
      resultado.imeis = imeis.map(imei => ({
        numero: imei && (imei.numero || imei.imei || imei.codigo || 'N/A'),
        modelo: imei && (imei.modelo || 'Desconhecido'),
        status: imei && (imei.status || 'ativo')
      }));
    }

    // Adiciona outros campos do dados, exceto os já processados
    Object.entries(dados).forEach(([key, value]) => {
      if (!['id', 'nome', 'descricao', 'data_criacao', 'imeis', 'imei'].includes(key)) {
        // Garante que não estamos adicionando objetos não serializáveis
        resultado[key] = value && typeof value === 'object' && !Array.isArray(value) 
          ? JSON.stringify(value) 
          : value;
      }
    });

    // Se houver uma mensagem de IMEIs não exibidos, adiciona ao resultado
    if (dados.mensagem) {
      resultado.mensagem = dados.mensagem;
    }

    return resultado;
  };

  const buscarProduto = async (codigo) => {
    if (!codigo.trim()) return;

    setCarregando(true);

    try {
      // Tenta fazer o parse do código como JSON
      try {
        const dados = JSON.parse(codigo);

        // Se for um cluster com IMEIs, processa os dados
        if (dados.imeis || dados.cluster_id) {
          const dadosProcessados = processarDadosDoCluster(dados);
          setResultado(dadosProcessados);
        } else {
          // Formata os dados para exibição genérica
          const dadosFormatados = {
            codigo: dados.codigo || dados.id || 'N/A',
            descricao: dados.descricao || dados.nome || 'Sem descrição',
            quantidade: dados.quantidade || dados.qtd || 1,
            quantidadeModelos: dados.modelos ? (Array.isArray(dados.modelos) ? dados.modelos.length : 1) : 1,
            localizacao: dados.localizacao || dados.local || 'Local não especificado',
            ultimaAtualizacao: dados.ultimaAtualizacao || new Date().toLocaleString(),
            // Adiciona campos adicionais dinamicamente
            ...Object.entries(dados)
              .filter(([key]) => !['codigo', 'descricao', 'quantidade', 'quantidadeModelos', 'localizacao', 'ultimaAtualizacao', 'id', 'nome', 'qtd', 'local', 'modelos', 'imeis'].includes(key))
              .reduce((acc, [key, value]) => ({
                ...acc,
                [key]: typeof value === 'object' ? JSON.stringify(value) : value
              }), {})
          };

          setResultado(dadosFormatados);
        }
      } catch (e) {
        // Se não for um JSON válido, tenta buscar na API
        console.log('Código não é um JSON válido, tentando buscar na API...');

        // Se for um UUID ou ID de cluster, busca os detalhes na API
        const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
        if (uuidRegex.test(codigo.trim()) || /^\d+$/.test(codigo.trim())) {
          try {
            const response = await fetch(`${API_ENDPOINTS.CLUSTERS}/${codigo.trim()}`, {
              method: 'GET',
              headers: {
                'accept': 'application/json'
              }
            });
            if (response.ok) {
              const dados = await response.json();
              const dadosProcessados = processarDadosDoCluster(dados);
              setResultado(dadosProcessados);
              return;
            }
          } catch (apiError) {
            console.error('Erro ao buscar dados da API:', apiError);
          }
        }

        // Se não encontrou na API, mostra como código simples
        setResultado({
          codigo: codigo,
          descricao: 'Código lido com sucesso',
          tipo: 'Código simples',
          dataLeitura: new Date().toLocaleString()
        });
      }

    } catch (error) {
      console.error('Erro ao processar código:', error);
      setResultado({
        erro: 'Erro ao processar o código',
        detalhes: error.message,
        codigoLido: codigo
      });
    } finally {
      setCarregando(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    buscarProduto(consulta);
  };

  return (
    <div className="consultar">
      <h2>Consultar Etiqueta</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="codigoConsulta">Código da Etiqueta:</label>
          <div className="search-container">
            <input
              type="text"
              id="codigoConsulta"
              value={consulta}
              onChange={handleChange}
              placeholder="Digite o código ou escaneie"
              required
            />
            <button 
              type="button" 
              className="camera-button"
              onClick={mostrarCamera ? pararCamera : iniciarCamera}
              disabled={carregando}
              title={mostrarCamera ? 'Parar câmera' : 'Ler código de barras/QR code'}
            >
              {mostrarCamera ? 'Parar Câmera' : 'Ler Código'}
            </button>
            <button 
              type="submit" 
              className="search-button"
              disabled={!consulta.trim() || carregando}
            >
              {carregando ? 'Buscando...' : 'Buscar'}
            </button>
          </div>
        </div>
      </form>

      {mostrarCamera && (
        <div className="camera-container">
          <div id="scanner" key={mostrarCamera ? 'scanner-active' : 'scanner-inactive'} className="scanner"></div>
          <p className="camera-instruction">Posicione o código de barras ou QR code na área de leitura</p>
          {erroCamera && <p className="error-message">{erroCamera}</p>}
        </div>
      )}

      {resultado && (
        <div className="resultado-consulta">
          <h3>Resultado da Consulta</h3>
          <div className="detalhes">
            {resultado.erro ? (
              <div className="erro-consulta">
                <p className="erro-titulo">❌ {resultado.erro}</p>
                {resultado.detalhes && <p className="erro-detalhe">{resultado.detalhes}</p>}
                {resultado.codigoLido && <p className="erro-codigo">Código lido: <code>{resultado.codigoLido}</code></p>}
              </div>
            ) : (
              <>
                <div className="cabecalho-consulta">
                  <h4>{resultado.nome || 'Detalhes do Item'}</h4>
                  {(resultado.quantidadeImeis !== undefined || resultado.quantidadeModelos !== undefined) && (
                    <div className="resumo-quantidades">
                      {resultado.quantidadeImeis !== undefined && (
                        <div className="quantidade-item">
                          <span className="quantidade-numero">{resultado.quantidadeImeis}</span>
                          <span className="quantidade-rotulo">IMEIs</span>
                        </div>
                      )}
                      {resultado.quantidadeModelos !== undefined && (
                        <div className="quantidade-item">
                          <span className="quantidade-numero">{resultado.quantidadeModelos}</span>
                          <span className="quantidade-rotulo">Modelos</span>
                        </div>
                      )}
                    </div>
                  )}
                </div>

                <div className="detalhes-grid">
                  {Object.entries(resultado)
                    .filter(([key]) => !['quantidadeModelos', 'codigo'].includes(key))
                    .map(([chave, valor]) => {
                      // Formatação especial para alguns campos
                      if (chave === 'dataCriacao' && valor) {
                        valor = new Date(valor).toLocaleDateString('pt-BR');
                      }

                      return (
                        <div key={chave} className="detalhe-item">
                          <span className="detalhe-chave">
                            {chave
                              .replace(/([A-Z])/g, ' $1')
                              .replace(/^./, str => str.toUpperCase())
                              .replace('Quantidade ', '')
                            }:
                          </span>
                          <span className="detalhe-valor">
                            {typeof valor === 'boolean' 
                              ? (valor ? 'Sim' : 'Não') 
                              : Array.isArray(valor) 
                                ? `${valor.length} itens` 
                                : valor}
                          </span>
                        </div>
                      );
                    })}
                </div>

                {resultado.imeis && Array.isArray(resultado.imeis) && resultado.imeis.length > 0 && (
                  <div className="lista-imeis">
                    <h4>Lista de IMEIs</h4>
                    <div className="tabela-imeis">
                      <div className="cabecalho-tabela">
                        <div>IMEI</div>
                        <div>Modelo</div>
                        <div>Status</div>
                      </div>
                      {resultado.imeis.slice(0, 5).map((imei, index) => (
                        <div key={index} className="linha-imei">
                          <div>{imei.numero || 'N/A'}</div>
                          <div>{imei.modelo || 'Desconhecido'}</div>
                          <div className={`status ${imei.status || 'ativo'}`}>
                            {imei.status === 'inativo' ? 'Inativo' : 'Ativo'}
                          </div>
                        </div>
                      ))}
                      {resultado.imeis.length > 5 && (
                        <div className="mais-itens">
                          +{resultado.imeis.length - 5} IMEIs adicionais
                        </div>
                      )}
                    </div>
                  </div>
                )}

                <div className="metadados">
                  <p>Consulta realizada em: {new Date().toLocaleString()}</p>
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Consultar;
