import React, { useMemo, useState } from 'react';

function CriarEtiqueta() {
  const [imei, setImei] = useState('');
  const [tocado, setTocado] = useState(false);

  const valido = useMemo(() => {
    if (!/^[0-9]{15}$/.test(imei)) return false;
    let sum = 0;
    for (let i = 0; i < 14; i++) {
      let digit = parseInt(imei[i], 10);
      if (i % 2 === 1) {
        digit *= 2;
        if (digit > 9) digit -= 9;
      }
      sum += digit;
    }
    const check = (10 - (sum % 10)) % 10;
    return check === parseInt(imei[14], 10);
  }, [imei]);

  const handlePrint = () => {
    if (!valido) return;
    const w = window.open('', '_blank', 'width=600,height=400');
    if (!w) return;
    const html = `<!doctype html>
<html>
<head>
<meta charset="utf-8" />
<title>Etiqueta IMEI</title>
<style>
  body { font-family: Arial, sans-serif; margin: 0; padding: 16px; }
  .label { width: 320px; border: 1px solid #111; padding: 16px; border-radius: 8px; }
  .title { font-size: 14px; color: #333; margin: 0 0 8px; }
  .imei { font-size: 20px; font-weight: 700; letter-spacing: 1px; margin: 8px 0 12px; }
  .row { display: flex; align-items: center; gap: 12px; }
  .meta { font-size: 12px; color: #555; margin-top: 8px; }
  .qr { width: 120px; height: 120px; object-fit: contain; }
  @media print { body { padding: 0; } .label { border: none; } }
</style>
</head>
<body onload="window.print()">
  <div class="label">
    <h2 class="title">Etiqueta de IMEI</h2>
    <div class="row">
      <img class="qr" src="https://api.qrserver.com/v1/create-qr-code/?size=120x120&data=${encodeURIComponent(imei)}" alt="QR" />
      <div>
        <div class="imei">${imei}</div>
        <div class="meta">Gerado em: ${new Date().toLocaleString()}</div>
      </div>
    </div>
  </div>
</body>
</html>`;
    w.document.open();
    w.document.write(html);
    w.document.close();
  };

  return (
    <div className="criar-etiqueta" style={{ maxWidth: 480, margin: '0 auto' }}>
      <h1>Criar Etiqueta por IMEI</h1>
      <div className="form" style={{ display: 'grid', gap: 12 }}>
        <input
          type="text"
          inputMode="numeric"
          placeholder="Digite o IMEI (15 dígitos)"
          value={imei}
          onChange={(e) => setImei(e.target.value.replace(/\D/g, ''))}
          onBlur={() => setTocado(true)}
          className="form-control"
          style={{ padding: '10px 12px', fontSize: 16 }}
        />
        {tocado && imei && !valido && (
          <div className="alert alert-warning">IMEI inválido. Verifique os 15 dígitos.</div>
        )}
        <button
          disabled={!valido}
          onClick={handlePrint}
          className={`btn ${valido ? 'btn-primary' : 'btn-secondary'}`}
          style={{ padding: '10px 14px', fontWeight: 600 }}
        >
          Imprimir Etiqueta
        </button>
      </div>
      {valido && (
        <div className="preview" style={{ marginTop: 20 }}>
          <div style={{ display: 'inline-block', border: '1px solid #ccc', borderRadius: 8, padding: 12 }}>
            <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
              <img
                className="qr"
                width={100}
                height={100}
                alt="QR Preview"
                src={`https://api.qrserver.com/v1/create-qr-code/?size=100x100&data=${encodeURIComponent(imei)}`}
              />
              <div>
                <div style={{ fontSize: 18, fontWeight: 700 }}>{imei}</div>
                <div style={{ fontSize: 12, color: '#666' }}>Pré-visualização</div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default CriarEtiqueta;
