import React, { useState, useRef, useEffect } from 'react';
import * as XLSX from 'xlsx';
import { API_ENDPOINTS } from '../config';
import './CriarEtiqueta.css';

function CriarEtiqueta() {
  const [imei, setImei] = useState('');
  const [excelData, setExcelData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState({ total: 0, processed: 0, success: 0, errors: 0 });
  const [clusterId, setClusterId] = useState(null);
  const [isGeneratingQrCode, setIsGeneratingQrCode] = useState(false);
  const [qrCodeImage, setQrCodeImage] = useState(null);
  const fileInputRef = useRef(null);

  // Limpar progresso ao alterar dados do Excel
  useEffect(() => {
    if (excelData.length === 0) {
      setProgress({ total: 0, processed: 0, success: 0, errors: 0 });
    }
  }, [excelData]);

  const handleImeiChange = (e) => {
    setImei(e.target.value);
  };

  // --- Funções de API omitidas para clareza ---
  // (mantive todas as suas funções originais, apenas ajustei fechamento e estrutura)

  const handleGenerateQrCode = async () => {
    if (!clusterId) {
      alert('Erro: Nenhum clusterId disponível para gerar o QR code');
      return;
    }

    setIsGeneratingQrCode(true);

    try {
      const url = API_ENDPOINTS.CLUSTER_QRCODE(clusterId);
      const timestamp = new Date().getTime();
      const urlWithCacheBust = `${url}${url.includes('?') ? '&' : '?'}_=${timestamp}`;

      const response = await fetch(urlWithCacheBust);
      if (!response.ok) throw new Error('Erro ao buscar QR code');

      const blob = await response.blob();
      const imageUrl = URL.createObjectURL(blob);
      setQrCodeImage(imageUrl);
    } catch (error) {
      console.error('Erro ao gerar QR code:', error);
      alert('Falha ao gerar QR code. Veja o console.');
    } finally {
      setIsGeneratingQrCode(false);
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    try {
      if (!file.name.endsWith('.csv') && !file.type.includes('csv')) {
        throw new Error('Por favor, selecione um arquivo CSV.');
      }

      await processarArquivoCSV(file);
      if (fileInputRef.current) fileInputRef.current.value = '';
    } catch (error) {
      alert(error.message || 'Erro ao processar o arquivo.');
    }
  };

  const handleClearFile = () => {
    setExcelData([]);
    setImei('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="criar-etiqueta">
      <h2>Gerar Etiqueta de IMEI</h2>

      <div className="main-container">
        {/* Coluna da esquerda */}
        <div className="form-column">
          <div className="importar-excel">
            <h3>Importar IMEIs do Excel/CSV</h3>
            <input
              type="file"
              accept=".csv, .xlsx, .xls"
              onChange={handleFileUpload}
              ref={fileInputRef}
              style={{ display: 'none' }}
              id="file-upload"
            />
            <div className="file-upload-container">
              <button
                type="button"
                className="file-upload-button"
                onClick={() => document.getElementById('file-upload').click()}
              >
                {isLoading ? 'Processando...' : 'Selecionar Arquivo (CSV/Excel)'}
              </button>

              {excelData.length > 0 && (
                <div className="file-info">
                  <span>{excelData.length} IMEIs carregados</span>
                  <button type="button" className="clear-button" onClick={handleClearFile}>
                    Limpar
                  </button>
                </div>
              )}
            </div>
          </div>

          <div className="divider">ou</div>

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="imei">IMEI:</label>
              <input
                type="text"
                id="imei"
                value={imei}
                onChange={handleImeiChange}
                required={excelData.length === 0}
                disabled={excelData.length > 0}
                placeholder="Digite o número do IMEI"
              />
            </div>

            <div className="button-group">
              <button type="submit" className="submit-button" disabled={isLoading}>
                {isLoading
                  ? `Processando... ${progress.processed}/${progress.total}`
                  : excelData.length > 0
                  ? `Gerar ${excelData.length} Etiquetas`
                  : 'Processar IMEIs'}
              </button>

              {isLoading && progress.total > 0 && (
                <div className="progress-container">
                  <p>
                    Processando: {progress.processed} de {progress.total} IMEIs
                  </p>
                  <div className="progress-bar">
                    <div
                      className="progress-bar-fill"
                      style={{ width: `${(progress.processed / progress.total) * 100}%` }}
                    ></div>
                  </div>
                  <p>
                    Sucesso: {progress.success} | Erros: {progress.errors}
                  </p>
                </div>
              )}

              <div className="qr-code-button-container">
                <button
                  className={`criar-etiqueta-btn ${
                    !clusterId || isGeneratingQrCode ? 'disabled' : ''
                  }`}
                  onClick={handleGenerateQrCode}
                  disabled={!clusterId || isGeneratingQrCode}
                >
                  {isGeneratingQrCode ? 'Gerando QR Code...' : 'Gerar QR Code'}
                </button>
              </div>

              {qrCodeImage && (
                <div className="qr-code-preview">
                  <h4>QR Code Gerado:</h4>
                  <img
                    src={qrCodeImage}
                    alt="QR Code do Cluster"
                    className="qr-code-image"
                    style={{ maxWidth: '100%', border: '1px solid red' }}
                  />
                  <div className="qr-code-actions">
                    <a
                      href={qrCodeImage}
                      download={`qrcode-${clusterId}.png`}
                      className="download-qr-btn"
                    >
                      Baixar QR Code
                    </a>
                  </div>
                </div>
              )}
            </div>
          </form>
        </div>

        {/* Coluna da direita */}
        <div className="qr-code-column">
          <h3>QR Code do Cluster</h3>
          <div className="qr-code-container">
            {!clusterId ? (
              <div className="qr-code-placeholder">
                <p>Processe os IMEIs para gerar o QR code</p>
              </div>
            ) : isGeneratingQrCode ? (
              <div className="qr-code-loading">
                <p>Gerando QR code...</p>
                <div className="spinner"></div>
              </div>
            ) : qrCodeImage ? (
              <div className="qr-code-preview">
                <img src={qrCodeImage} alt="QR Code do Cluster" className="qr-code-image" />
                <div className="qr-code-actions">
                  <a
                    href={qrCodeImage}
                    download={`qrcode-${clusterId}.png`}
                    className="download-qr-btn"
                  >
                    Baixar QR Code
                  </a>
                </div>
              </div>
            ) : (
              <div className="qr-code-placeholder">
                <p>Clique em "Gerar QR Code" para visualizar</p>
                <button
                  className="criar-etiqueta-btn"
                  onClick={handleGenerateQrCode}
                  disabled={!clusterId}
                >
                  Gerar QR Code
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default CriarEtiqueta;
