import React, { useState, useRef, useEffect } from 'react';
import './Consultar.css';
import { Html5Qrcode } from 'html5-qrcode';
import { API_ENDPOINTS } from '../config';

function Consultar() {
  // Adiciona um manipulador de erros global para este componente
  useEffect(() => {
    const handleError = (event) => {
      // Ignora erros de aborto de requisição e erros de câmera específicos
      const errorMessage = (event.error?.message || event.message || '').toLowerCase();
      const shouldIgnore = 
        errorMessage.includes('aborted') ||
        errorMessage.includes('camera') ||
        errorMessage.includes('permission') ||
        event?.type === 'abort';

      if (shouldIgnore && !errorMessage.includes('critical')) {
        console.warn('Aviso:', errorMessage || 'Operação interrompida (pode ser esperado)');
        return;
      }

      // Log mais detalhado para erros não ignorados
      console.error('Erro não tratado:', {
        error: event.error || event,
        message: event.message,
        type: event.type,
        timestamp: new Date().toISOString()
      });
    };

    // Adiciona os listeners
    const errorListener = (event) => {
      try {
        handleError(event);
      } catch (e) {
        console.error('Erro no manipulador de erros:', e);
      }
    };

    const unhandledRejectionListener = (event) => {
      try {
        handleError(event);
      } catch (e) {
        console.error('Erro no manipulador de rejeições não tratadas:', e);
      }
    };

    window.addEventListener('error', errorListener);
    window.addEventListener('unhandledrejection', unhandledRejectionListener);

    // Limpa os listeners quando o componente for desmontado
    return () => {
      try {
        window.removeEventListener('error', errorListener);
        window.removeEventListener('unhandledrejection', unhandledRejectionListener);
      } catch (e) {
        console.error('Erro ao remover listeners:', e);
      }
    };
  }, []);
  const [consulta, setConsulta] = useState('');
  const [resultado, setResultado] = useState(null);
  const [carregando, setCarregando] = useState(false);
  const [mostrarCamera, setMostrarCamera] = useState(false);
  const [erroCamera, setErroCamera] = useState('');
  const [cameraPronta, setCameraPronta] = useState(false);

  // Refs para controle de estado assíncrono
  const iniciandoCameraRef = useRef(false);
  const parandoCameraRef = useRef(false);
  const html5QrCodeRef = useRef(null);

  const handleChange = (e) => {
    setConsulta(e.target.value);
  };

  useEffect(() => {
    // Função para limpar recursos quando o componente for desmontado
    return () => {
      console.log('Componente será desmontado, limpando recursos...');
      // Usamos setTimeout para evitar problemas de sincronização
      setTimeout(() => {
        pararCamera(true).catch(() => {
          // Ignora erros durante a limpeza
        });
      }, 0);
    };
  }, []);

  const iniciarCamera = async () => {
    console.log('=== INICIANDO CÂMERA ===');
    console.log('1. Verificando se já está inicializando...');

    // Se já estiver inicializando, não faz nada
    if (iniciandoCameraRef.current) {
      console.log('1.1. Já está em processo de inicialização, saindo...');
      return;
    }

    console.log('2. Marcando como inicializando...');
    iniciandoCameraRef.current = true;

    try {
      setMostrarCamera(true);
      setErroCamera('Preparando a câmera...');

      console.log('3. Configurando o scanner...');
      // Configuração mínima
      const config = { 
        fps: 10,
        qrbox: 200,
        experimentalFeatures: {
          useBarCodeDetectorIfSupported: true
        },
        // Desativa o scanner de códigos de barras para melhorar a performance
        disableFlip: true,
        showTorchButtonIfSupported: true
      };

      console.log('4. Configuração do scanner:', JSON.stringify(config, null, 2));

      console.log('5. Verificando elemento do scanner no DOM...');
      // Pequeno atraso para garantir que o elemento esteja no DOM
      await new Promise(resolve => setTimeout(resolve, 500));

      // Verifica se o elemento existe
      const scannerElement = document.getElementById('scanner');
      console.log('6. Elemento do scanner encontrado:', !!scannerElement);

      if (!scannerElement) {
        console.error('6.1. ERRO: Elemento do scanner não encontrado no DOM');
        throw new Error('Elemento do scanner não encontrado');
      }

      console.log('7. Limpando conteúdo anterior do scanner...');
      // Limpa o conteúdo do scanner para evitar duplicações
      const childCount = scannerElement.children.length;
      while (scannerElement.firstChild) {
        scannerElement.removeChild(scannerElement.firstChild);
      }
      console.log(`7.1. Removidos ${childCount} elementos filhos do scanner`);

      console.log('8. Criando container do scanner...');
      // Cria um novo container para o scanner
      const scannerContainer = document.createElement('div');
      scannerContainer.id = 'scanner-container';
      scannerContainer.style.width = '100%';
      scannerContainer.style.height = '100%';
      scannerElement.appendChild(scannerContainer);
      console.log('8.1. Container do scanner criado com sucesso');

      // Cria nova instância
      console.log('9. Criando nova instância do Html5Qrcode...');
      const html5QrCode = new Html5Qrcode('scanner-container');
      console.log('9.1. Instância do Html5Qrcode criada com sucesso');

      // Função de sucesso
      const onScanSuccess = (decodedText) => {
        console.log('Código lido:', decodedText);
        setConsulta(decodedText);
        pararCamera();
        buscarProduto(decodedText);
      };

      // Tenta iniciar a câmera
      try {
        console.log('10. Iniciando tentativa de inicialização da câmera...');

        // Tenta primeiro com a câmera traseira
        console.log('10.1. Tentando iniciar câmera traseira (environment)...');
        try {
          await html5QrCode.start(
            { facingMode: 'environment' },
            config,
            onScanSuccess,
            (error) => {
              // Ignora erros comuns
              if (error && 
                  !error.message?.includes('No MultiFormat Readers') && 
                  !error.message?.includes('No QR code found')) {
                console.warn('Aviso do scanner:', error);
              }
            }
          );

          // Se chegou aqui, a câmera foi iniciada com sucesso
          html5QrCodeRef.current = html5QrCode;
          setErroCamera('Aponte para o código QR');
          setCameraPronta(true);
          console.log('10.2. Câmera traseira iniciada com sucesso!');
          return;

        } catch (err) {
          console.warn('10.3. Falha ao iniciar câmera traseira, tentando frontal...', err.message || err);

          // Tenta com a câmera frontal
          console.log('10.4. Tentando iniciar câmera frontal (user)...');
          try {
            await html5QrCode.start(
              { facingMode: 'user' },
              config,
              onScanSuccess,
              (error) => {
                if (error && 
                    !error.message?.includes('No MultiFormat Readers') && 
                    !error.message?.includes('No QR code found')) {
                  console.warn('Aviso do scanner frontal:', error);
                }
              }
            );

            html5QrCodeRef.current = html5QrCode;
            setErroCamera('Aponte para o código QR (câmera frontal)');
            setCameraPronta(true);
            console.log('10.5. Câmera frontal iniciada com sucesso!');
            return;

          } catch (frontError) {
            console.error('10.6. Falha ao iniciar câmera frontal:', frontError.message || frontError);
            throw new Error('Não foi possível acessar nenhuma câmera');
          }
        }
      } catch (startError) {
        console.error('11. ERRO ao iniciar a câmera:', startError);
        throw startError;
      }

    } catch (err) {
      console.error('13. ERRO no processo de inicialização da câmera:', err);
      setErroCamera(`Não foi possível acessar a câmera: ${err.message || 'Erro desconhecido'}`);
      setMostrarCamera(false);

      // Tenta limpar recursos em caso de erro
      try {
        await pararCamera(true);
      } catch (cleanupError) {
        console.warn('Erro durante limpeza:', cleanupError);
      }

    } finally {
      console.log('14. Finalizando processo de inicialização da câmera');
      iniciandoCameraRef.current = false;
      console.log('=== FIM DO PROCESSO DE INICIALIZAÇÃO ===\n');
    }
  };

  const pararCamera = async (shouldSuppressErrors = false) => {
    // Se já estiver parando, não faz nada
    if (parandoCameraRef.current) {
      console.log('1.1. Já está em processo de parada, saindo...');
      return Promise.resolve();
    }

    console.log('\n=== INICIANDO PARADA DA CÂMERA ===');
    console.log('1. Iniciando processo de parada...');

    parandoCameraRef.current = true;
    setCameraPronta(false);

    try {
      console.log('2. Verificando se há uma instância ativa do scanner...');

      if (html5QrCodeRef.current) {
        console.log('3. Parando o scanner...');
        try {
          await html5QrCodeRef.current.stop();
          console.log('3.1. Scanner parado com sucesso');
        } catch (stopError) {
          if (!shouldSuppressErrors) {
            console.warn('3.2. Aviso ao parar o scanner:', stopError.message);
          }
          // Continua mesmo com erro ao parar
        } finally {
          // Limpa a referência mesmo se houver erro
          html5QrCodeRef.current = null;
        }
      } else {
        console.log('3. Nenhuma instância ativa do scanner encontrada');
      }

      // Limpa o container do scanner
      console.log('4. Limpando o container do scanner...');
      const scannerElement = document.getElementById('scanner');

      if (scannerElement) {
        console.log('4.1. Removendo elementos filhos do scanner...');

        // Remove todos os filhos do scanner de forma segura
        try {
          while (scannerElement.firstChild) {
            scannerElement.removeChild(scannerElement.firstChild);
          }
          console.log('4.2. Limpeza do DOM concluída');
        } catch (cleanupError) {
          if (!shouldSuppressErrors) {
            console.warn('4.3. Aviso ao limpar o scanner:', cleanupError.message);
          }
        }
      } else {
        console.log('4.1. Elemento do scanner não encontrado no DOM');
      }

      console.log('5. Câmera parada com sucesso');
      return Promise.resolve();

    } catch (error) {
      if (!shouldSuppressErrors) {
        console.error('6. ERRO ao parar a câmera:', {
          message: error.message,
          name: error.name,
          stack: error.stack
        });
      }
      return Promise.reject(error);
    } finally {
      console.log('7. Finalizando processo de parada...');
      setMostrarCamera(false);
      parandoCameraRef.current = false;
      console.log('=== FIM DO PROCESSO DE PARADA ===\n');
    }
  };

  const contarModelosUnicos = (imeis) => {
    if (!imeis || !Array.isArray(imeis)) return 0;
    const modelos = new Set(imeis.map(imei => imei.modelo || '').filter(Boolean));
    return modelos.size;
  };

  const processarDadosDoCluster = (dados) => {
    if (!dados || typeof dados !== 'object') {
      return {
        erro: 'Dados inválidos',
        detalhes: 'Os dados recebidos não estão no formato esperado'
      };
    }

    // Processa os IMEIs - pode ser um array de strings ou objetos
    let imeis = [];
    if (Array.isArray(dados.imeis)) {
      imeis = dados.imeis.map(imei => {
        if (typeof imei === 'string') {
          return { numero: imei };
        } else if (typeof imei === 'object' && imei !== null) {
          return {
            numero: imei.numero || imei.imei || 'N/A',
            modelo: imei.modelo || 'Desconhecido',
            status: imei.status || 'ativo'
          };
        }
        return { numero: 'Formato inválido' };
      });
    }

    // Cria o objeto de retorno com os dados do cluster
    const resultado = {
      id: dados.id || 'N/A',
      nome: dados.nome || 'Sem nome',
      descricao: dados.descricao || 'Sem descrição',
      dataCriacao: dados.data_criacao ? new Date(dados.data_criacao).toLocaleString() : 'Data não disponível',
      totalImeis: dados.total_imeis || imeis.length,
      quantidadeImeis: imeis.length,
      quantidadeModelos: contarModelosUnicos(imeis),
      imeis: imeis
    };

    // Adiciona campos adicionais que não foram mapeados
    const camposIgnorados = [
      'id', 'nome', 'descricao', 'data_criacao', 'imeis', 'total_imeis',
      'created_at', 'updated_at', 'total_imeis'
    ];

    Object.entries(dados).forEach(([key, value]) => {
      if (!camposIgnorados.includes(key)) {
        // Formata valores especiais
        if (value === null || value === undefined) {
          resultado[key] = 'N/A';
        } else if (typeof value === 'object' && !Array.isArray(value)) {
          resultado[key] = JSON.stringify(value);
        } else if (Array.isArray(value)) {
          resultado[key] = value.join(', ');
        } else if (typeof value === 'boolean') {
          resultado[key] = value ? 'Sim' : 'Não';
        } else {
          resultado[key] = value;
        }
      }
    });

    return resultado;
  };

  const buscarProduto = async (codigo) => {
    if (!codigo || !codigo.trim()) {
      console.error('Código vazio ou inválido');
      return;
    }

    // Log para debug
    console.log('URL da API:', API_ENDPOINTS.CLUSTERS);

    setCarregando(true);
    setResultado(null);

    // Cria um controlador para poder cancelar a requisição se necessário
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 10000); // Timeout de 10 segundos

    try {
      // Tenta fazer o parse do código como JSON
      try {
        const dados = JSON.parse(codigo);
        console.log('Dados do JSON:', dados);

        // Se for um cluster com IMEIs, processa os dados
        if (dados.imeis || dados.cluster_id) {
          const dadosProcessados = processarDadosDoCluster(dados);
          setResultado(dadosProcessados);
          clearTimeout(timeoutId);
          return;
        } else {
          // Formata os dados para exibição genérica
          const dadosFormatados = {
            codigo: dados.codigo || dados.id || 'N/A',
            descricao: dados.descricao || dados.nome || 'Sem descrição',
            quantidade: dados.quantidade || dados.qtd || 1,
            quantidadeModelos: dados.modelos ? (Array.isArray(dados.modelos) ? dados.modelos.length : 1) : 1,
            localizacao: dados.localizacao || dados.local || 'Local não especificado',
            ultimaAtualizacao: dados.ultimaAtualizacao || new Date().toLocaleString(),
            // Adiciona campos adicionais dinamicamente
            ...Object.entries(dados)
              .filter(([key]) => !['codigo', 'descricao', 'quantidade', 'quantidadeModelos', 'localizacao', 'ultimaAtualizacao', 'id', 'nome', 'qtd', 'local', 'modelos', 'imeis'].includes(key))
              .reduce((acc, [key, value]) => ({
                ...acc,
                [key]: typeof value === 'object' ? JSON.stringify(value) : value
              }), {})
          };

          setResultado(dadosFormatados);
          clearTimeout(timeoutId);
          return;
        }
      } catch (e) {
        // Se não for um JSON válido, continua para a busca na API
        console.log('Código não é um JSON válido, tentando buscar na API...');
      }

      // Se chegou aqui, tenta buscar na API
      const codigoLimpo = codigo.trim();

      // Verifica se parece ser um UUID ou ID numérico
      const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
      const isUuid = uuidRegex.test(codigoLimpo);
      const isNumericId = /^\d+$/.test(codigoLimpo);

      if (isUuid || isNumericId) {
        try {
          console.log(`Buscando cluster com ID: ${codigoLimpo} em ${API_ENDPOINTS.CLUSTERS}`);

          const url = `${API_ENDPOINTS.CLUSTERS}/${codigoLimpo}`;
          console.log('Fazendo requisição para:', url);

          const response = await fetch(url, {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
              'ngrok-skip-browser-warning': 'true' // Adiciona header para evitar avisos do ngrok
            },
            credentials: 'include',
            signal: controller.signal,
            mode: 'cors' // Força o modo CORS
          });

          clearTimeout(timeoutId);

          console.log('Resposta da API:', response);

          if (!response.ok) {
            const errorData = await response.json().catch(() => ({}));
            throw new Error(errorData.message || `Erro ${response.status} ao buscar dados`);
          }

          const dados = await response.json();
          console.log('Dados recebidos da API:', dados);

          const dadosProcessados = processarDadosDoCluster(dados);
          setResultado(dadosProcessados);
          return;

        } catch (apiError) {
          // Verifica se foi um erro de timeout ou cancelamento
          if (apiError.name === 'AbortError') {
            console.warn('A requisição foi cancelada por timeout');
            setResultado({
              erro: 'Tempo limite excedido',
              detalhes: 'A requisição demorou muito para responder. Verifique sua conexão e tente novamente.',
              codigoLido: codigoLimpo
            });
          } else {
            console.error('Erro ao buscar dados da API:', apiError);
            setResultado({
              erro: 'Erro ao buscar dados',
              detalhes: apiError.message || 'Não foi possível conectar ao servidor',
              codigoLido: codigoLimpo
            });
          }
          return;
        }
      }

      // Se chegou aqui, é um código simples
      console.log('Código simples lido:', codigoLimpo);
      setResultado({
        codigo: codigoLimpo,
        descricao: 'Código lido com sucesso',
        tipo: 'Código simples',
        dataLeitura: new Date().toLocaleString()
      });

    } catch (error) {
      console.error('Erro ao processar código:', error);
      setResultado({
        erro: 'Erro ao processar código',
        detalhes: error.message || 'Erro desconhecido',
        codigoLido: codigo
      });
    } finally {
      clearTimeout(timeoutId);
      setCarregando(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    buscarProduto(consulta);
  };

  return (
    <div className="consultar">
      <h2>Consultar Etiqueta</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="codigoConsulta">Código da Etiqueta:</label>
          <div className="search-container">
            <input
              type="text"
              id="codigoConsulta"
              value={consulta}
              onChange={handleChange}
              placeholder="Digite o código ou escaneie"
              required
            />
            <button 
              type="button" 
              className="camera-button"
              onClick={mostrarCamera ? pararCamera : iniciarCamera}
              disabled={carregando}
              title={mostrarCamera ? 'Parar câmera' : 'Ler código de barras/QR code'}
            >
              {mostrarCamera ? 'Parar Câmera' : 'Ler Código'}
            </button>
            <button 
              type="submit" 
              className="search-button"
              disabled={!consulta.trim() || carregando}
            >
              {carregando ? 'Buscando...' : 'Buscar'}
            </button>
          </div>
        </div>
      </form>

      {mostrarCamera && (
        <div className="camera-container">
          <div 
            id="scanner" 
            key={Date.now()} // Força o recarregamento do componente
            style={{ width: '100%', height: '300px' }}
          ></div>
          <p className="camera-instruction">
            {cameraPronta 
              ? 'Posicione o código de barras ou QR code na área de leitura'
              : 'Iniciando câmera...'}
          </p>
          {erroCamera && <p className="error-message">{erroCamera}</p>}
        </div>
      )}

      {resultado && (
        <div className="resultado-consulta">
          <h3>Resultado da Consulta</h3>
          <div className="detalhes">
            {resultado.erro ? (
              <div className="erro-consulta">
                <p className="erro-titulo">❌ {resultado.erro}</p>
                {resultado.detalhes && <p className="erro-detalhe">{resultado.detalhes}</p>}
                {resultado.codigoLido && <p className="erro-codigo">Código lido: <code>{resultado.codigoLido}</code></p>}
              </div>
            ) : (
              <>
                <div className="cabecalho-consulta">
                  <h4>{resultado.nome || 'Detalhes do Item'}</h4>
                  {(resultado.quantidadeImeis !== undefined || resultado.quantidadeModelos !== undefined) && (
                    <div className="resumo-quantidades">
                      {resultado.quantidadeImeis !== undefined && (
                        <div className="quantidade-item">
                          <span className="quantidade-numero">{resultado.quantidadeImeis}</span>
                          <span className="quantidade-rotulo">IMEIs</span>
                        </div>
                      )}
                      {resultado.quantidadeModelos !== undefined && (
                        <div className="quantidade-item">
                          <span className="quantidade-numero">{resultado.quantidadeModelos}</span>
                          <span className="quantidade-rotulo">Modelos</span>
                        </div>
                      )}
                    </div>
                  )}
                </div>

                <div className="detalhes-grid">
                  {Object.entries(resultado)
                    .filter(([key]) => !['quantidadeModelos', 'codigo'].includes(key))
                    .map(([chave, valor]) => {
                      // Formatação especial para alguns campos
                      if (chave === 'dataCriacao' && valor) {
                        valor = new Date(valor).toLocaleDateString('pt-BR');
                      }

                      return (
                        <div key={chave} className="detalhe-item">
                          <span className="detalhe-chave">
                            {chave
                              .replace(/([A-Z])/g, ' $1')
                              .replace(/^./, str => str.toUpperCase())
                              .replace('Quantidade ', '')
                            }:
                          </span>
                          <span className="detalhe-valor">
                            {typeof valor === 'boolean' 
                              ? (valor ? 'Sim' : 'Não') 
                              : Array.isArray(valor) 
                                ? `${valor.length} itens` 
                                : valor}
                          </span>
                        </div>
                      );
                    })}
                </div>

                {resultado.imeis && Array.isArray(resultado.imeis) && resultado.imeis.length > 0 && (
                  <div className="lista-imeis">
                    <h4>Lista de IMEIs</h4>
                    <div className="tabela-imeis">
                      <div className="cabecalho-tabela">
                        <div>IMEI</div>
                        <div>Modelo</div>
                        <div>Status</div>
                      </div>
                      {resultado.imeis.slice(0, 5).map((imei, index) => (
                        <div key={index} className="linha-imei">
                          <div>{imei.numero || 'N/A'}</div>
                          <div>{imei.modelo || 'Desconhecido'}</div>
                          <div className={`status ${imei.status || 'ativo'}`}>
                            {imei.status === 'inativo' ? 'Inativo' : 'Ativo'}
                          </div>
                        </div>
                      ))}
                      {resultado.imeis.length > 5 && (
                        <div className="mais-itens">
                          +{resultado.imeis.length - 5} IMEIs adicionais
                        </div>
                      )}
                    </div>
                  </div>
                )}

                <div className="metadados">
                  <p>Consulta realizada em: {new Date().toLocaleString()}</p>
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Consultar;
