import React, { useState, useRef, useEffect, useCallback } from 'react';
import './Consultar.css';
import { Html5Qrcode, Html5QrcodeSupportedFormats } from 'html5-qrcode';

// Configurações do scanner
const qrConfig = {
  fps: 10,
  qrbox: { width: 250, height: 250 },
  disableFlip: false,
  experimentalFeatures: { useBarCodeDetectorIfSupported: true },
  formatsToSupport: [
    Html5QrcodeSupportedFormats.QR_CODE,
    Html5QrcodeSupportedFormats.CODE_128,
    Html5QrcodeSupportedFormats.EAN_13
  ]
};

function Consultar() {
  const [consulta, setConsulta] = useState('');
  const [resultado, setResultado] = useState(null);
  const [carregando, setCarregando] = useState(false);
  const [mostrarCamera, setMostrarCamera] = useState(false);
  const [erroCamera, setErroCamera] = useState('');
  const [cameraAtiva, setCameraAtiva] = useState(false);

  const html5QrCodeRef = useRef(null);
  const scannerContainerRef = useRef(null);
  const iniciandoCameraRef = useRef(false);
  const parandoCameraRef = useRef(false);

  // Limpar scanner
  const limparScanner = useCallback(() => {
    try {
      const old = document.getElementById('scanner');
      if (old) {
        const newContainer = document.createElement('div');
        newContainer.id = 'scanner';
        newContainer.style.width = '100%';
        newContainer.style.height = '300px';
        old.replaceWith(newContainer);
        scannerContainerRef.current = newContainer;
      }
    } catch (error) {
      console.warn('Erro ao limpar scanner:', error);
    }
  }, []);

  // Parar câmera
  const pararCamera = useCallback(async () => {
    if (parandoCameraRef.current) return;
    parandoCameraRef.current = true;

    try {
      if (html5QrCodeRef.current) {
        try {
          await html5QrCodeRef.current.stop();
          console.log('Scanner parado com sucesso');
        } catch (err) {
          if (!err.message?.includes('not running')) {
            console.warn('Erro ao parar scanner:', err);
          }
        } finally {
          html5QrCodeRef.current = null;
        }
      }
      setCameraAtiva(false);
      setMostrarCamera(false);
    } catch (error) {
      console.error('Erro ao parar câmera:', error);
    } finally {
      parandoCameraRef.current = false;
    }
  }, []);

  // Iniciar câmera
  const iniciarCamera = useCallback(async () => {
    if (iniciandoCameraRef.current) return;
    iniciandoCameraRef.current = true;

    try {
      setErroCamera('');
      if (html5QrCodeRef.current) await pararCamera();

      setMostrarCamera(true);
      await new Promise(r => setTimeout(r, 100));

      const scannerEl = document.getElementById('scanner');
      if (!scannerEl) throw new Error('Elemento scanner não encontrado');

      scannerContainerRef.current = scannerEl;
      html5QrCodeRef.current = new Html5Qrcode('scanner', true);

      await html5QrCodeRef.current.start(
        { facingMode: 'environment' },
        qrConfig,
        (decodedText) => {
          console.log('Código lido:', decodedText);
          setConsulta(decodedText);
          buscarProduto(decodedText);
          pararCamera();
        },
        (errorMessage) => {
          if (!errorMessage.includes('No MultiFormat')) {
            console.warn('Erro na leitura:', errorMessage);
          }
        }
      );

      setCameraAtiva(true);
      console.log('Scanner iniciado com sucesso');
    } catch (error) {
      console.error('Erro ao iniciar câmera:', error);
      setErroCamera('Não foi possível acessar a câmera. Verifique as permissões.');
      await pararCamera();
    } finally {
      iniciandoCameraRef.current = false;
    }
  }, [pararCamera]);

  // Buscar produto
  const buscarProduto = async (codigo) => {
    if (!codigo?.trim()) return;
    try {
      setCarregando(true);
      setResultado(null);

      console.log('Buscando produto com código:', codigo);
      await new Promise(res => setTimeout(res, 1000));

      setResultado({
        codigo,
        nome: 'Produto de Exemplo',
        descricao: 'Este é um produto de exemplo',
        preco: 'R$ 99,99',
        dataConsulta: new Date().toLocaleString()
      });
    } catch (error) {
      console.error('Erro ao buscar produto:', error);
      setErroCamera('Erro ao buscar produto. Tente novamente.');
    } finally {
      setCarregando(false);
    }
  };

  // Limpeza no unmount
  useEffect(() => {
    return () => {
      console.log('Limpando recursos ao desmontar...');
      pararCamera();
    };
  }, [pararCamera]);

  return (
    <div className="consultar-container">
      <h1>Consultar Produto</h1>

      <div className="input-group">
        <input
          type="text"
          value={consulta}
          onChange={(e) => setConsulta(e.target.value)}
          placeholder="Digite o código ou escaneie o QR Code"
          className="form-control"
        />
        <button
          onClick={cameraAtiva ? pararCamera : iniciarCamera}
          className={`btn ${cameraAtiva ? 'btn-danger' : 'btn-primary'}`}
          disabled={iniciandoCameraRef.current || parandoCameraRef.current}
        >
          {cameraAtiva ? 'Parar Câmera' : 'Escanear QR Code'}
        </button>
      </div>

      {erroCamera && (
        <div className="alert alert-warning mt-3">{erroCamera}</div>
      )}

      <div id="scanner" className={`scanner-container ${mostrarCamera ? 'active' : ''}`}>
        {mostrarCamera && !cameraAtiva && (
          <div className="scanner-loading">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Carregando câmera...</span>
            </div>
            <p>Preparando a câmera...</p>
          </div>
        )}
      </div>

      {carregando && (
        <div className="text-center mt-3">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Carregando...</span>
          </div>
          <p>Buscando informações do produto...</p>
        </div>
      )}

      {resultado && (
        <div className="resultado-consulta mt-4">
          <h3>Resultado da Consulta</h3>
          <pre>{JSON.stringify(resultado, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default Consultar;
